import React, { useState, useEffect } from 'react';
import './Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    totalBlogs: 0,
    totalProjects: 0,
    recentBlogs: [],
    recentProjects: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Mock data for demo - replace with actual API calls
      setStats({
        totalBlogs: 12,
        totalProjects: 8,
        recentBlogs: [
          { id: 1, title: 'Getting Started with React' },
          { id: 2, title: 'Advanced JavaScript Patterns' },
          { id: 3, title: 'CSS Grid vs Flexbox' }
        ],
        recentProjects: [
          { id: 1, name: 'E-commerce Website' },
          { id: 2, name: 'Portfolio App' },
          { id: 3, name: 'Task Management Tool' }
        ]
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Blogs</h3>
          <p className="stat-number">{stats.totalBlogs}</p>
        </div>
        <div className="stat-card">
          <h3>Total Projects</h3>
          <p className="stat-number">{stats.totalProjects}</p>
        </div>
      </div>

      <div className="recent-activity">
        <div className="recent-section">
          <h3>Recent Blogs</h3>
          {stats.recentBlogs.length === 0 ? (
            <p>No blogs yet</p>
          ) : (
            <ul>
              {stats.recentBlogs.map(blog => (
                <li key={blog.id}>{blog.title}</li>
              ))}
            </ul>
          )}
        </div>
        
        <div className="recent-section">
          <h3>Recent Projects</h3>
          {stats.recentProjects.length === 0 ? (
            <p>No projects yet</p>
          ) : (
            <ul>
              {stats.recentProjects.map(project => (
                <li key={project.id}>{project.name}</li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
