// admin-panel/src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useCallback, useEffect } from 'react';
import { authAPI } from '../services/api';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ----- LOGIN -----
  const login = async (email, password) => {
    try {
      setLoading(true);
      setError(null);

      const response = await authAPI.login(email, password);
      console.log('✅ Login Response:', response); // Debug

      // Adjust according to backend response
      const userData = response.user || response.data?.user;
      const token = response.token || response.data?.token;

      if (!userData || !token) {
        throw new Error('Invalid login response from server');
      }

      setUser(userData);
      localStorage.setItem('adminToken', token);
      localStorage.setItem('adminUser', JSON.stringify(userData));

      return response;
    } catch (err) {
      console.error('❌ Login Error:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Login failed');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // ----- LOGOUT -----
  const logout = () => {
    setUser(null);
    localStorage.removeItem('adminToken');
    localStorage.removeItem('adminUser');
    window.location.href = '/login';
  };

  // ----- VERIFY TOKEN -----
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('adminToken');
      if (token) {
        const response = await authAPI.getCurrentUser(); // Correct API call
        console.log('✅ Verify Response:', response); // Debug

        const userData = response.user || response.data?.user;
        if (userData) {
          setUser(userData);
        } else {
          logout(); // Token invalid, logout
        }
      } else {
        setUser(null);
      }
    } catch (err) {
      console.error('❌ Verify Error:', err.response?.data || err.message);
      logout();
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  return (
    <AuthContext.Provider value={{
      user,
      loading,
      error,
      login,
      logout,
      checkAuth
    }}>
      {children}
    </AuthContext.Provider>
  );
};

// ----- CUSTOM HOOK -----
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
