const API_BASE_URL = 'http://localhost:3001/api';

class ApiService {
  async request(endpoint, options = {}) {
    const url = `${API_BASE_URL}${endpoint}`;
    const token = localStorage.getItem('adminToken');
    
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` }),
      },
      ...options,
    };

    if (options.body) {
      config.body = JSON.stringify(options.body);
    }

    try {
      const response = await fetch(url, config);
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Request failed');
      }
      
      return data;
    } catch (error) {
      throw error;
    }
  }

  // Blog endpoints
  async getBlogs() {
    // Mock data for demo
    return Promise.resolve([
      { id: 1, title: 'First Blog', excerpt: 'This is a blog excerpt', content: 'Full content here', published: true },
      { id: 2, title: 'Second Blog', excerpt: 'Another blog excerpt', content: 'More content here', published: false }
    ]);
  }

  async createBlog(blogData) {
    console.log('Creating blog:', blogData);
    return Promise.resolve({ id: Date.now(), ...blogData });
  }

  async updateBlog(id, blogData) {
    console.log('Updating blog:', id, blogData);
    return Promise.resolve({ id, ...blogData });
  }

  async deleteBlog(id) {
    console.log('Deleting blog:', id);
    return Promise.resolve({ success: true });
  }

  // Project endpoints
  async getProjects() {
    // Mock data for demo
    return Promise.resolve([
      { 
        id: 1, 
        name: 'Portfolio Website', 
        description: 'A personal portfolio website', 
        technologies: ['React', 'Node.js', 'MongoDB'],
        githubUrl: 'https://github.com/example/portfolio',
        liveUrl: 'https://portfolio.example.com',
        featured: true
      }
    ]);
  }

  async createProject(projectData) {
    console.log('Creating project:', projectData);
    return Promise.resolve({ id: Date.now(), ...projectData });
  }

  async updateProject(id, projectData) {
    console.log('Updating project:', id, projectData);
    return Promise.resolve({ id, ...projectData });
  }

  async deleteProject(id) {
    console.log('Deleting project:', id);
    return Promise.resolve({ success: true });
  }
}

// Create instance and assign to variable before exporting
const apiService = new ApiService();
export default apiService;
