// src/services/api.js

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Generic API call function
const apiCall = async (endpoint, options = {}) => {
  try {
    const url = `${API_BASE_URL}${endpoint}`;
    console.log(`ðŸ“¡ API Call: ${options.method || 'GET'} ${url}`);
    
    const response = await fetch(url, {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers,
      },
      ...options,
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error('âŒ API Call Failed:', error);
    throw error;
  }
};

// Blog API functions
export const blogAPI = {
  // Get all published blogs for frontend
  getPublishedBlogs: async () => {
    return await apiCall('/api/blog');
  },

  // Get single blog post
  getBlogPost: async (id) => {
    return await apiCall(`/api/blog/${id}`);
  },

  // âœ… BLOG ADMIN FUNCTIONS
  getAllBlogs: async () => {
    return await apiCall('/blog/admin/all');
  },

  createBlog: async (blogData) => {
    return await apiCall('/blog/admin/create', {
      method: 'POST',
      body: JSON.stringify(blogData),
    });
  },

  updateBlog: async (id, blogData) => {
    return await apiCall(`/blog/admin/update/${id}`, {
      method: 'PUT',
      body: JSON.stringify(blogData),
    });
  },

  deleteBlog: async (id) => {
    return await apiCall(`/blog/admin/delete/${id}`, {
      method: 'DELETE',
    });
  },
};

// Projects API functions
export const projectsAPI = {
  // Get all projects for frontend
  getProjects: async () => {
    return await apiCall('/api/projects');
  },

  // Get single project
  getProject: async (id) => {
    return await apiCall(`/api/projects/${id}`);
  },

  // âœ… PROJECT ADMIN FUNCTIONS (THESE ARE MISSING!)
  getAllProjects: async () => {
    return await apiCall('/projects/admin/all');
  },

  createProject: async (projectData) => {
    return await apiCall('/projects/admin/create', {
      method: 'POST',
      body: JSON.stringify(projectData),
    });
  },

  updateProject: async (id, projectData) => {
    return await apiCall(`/projects/admin/update/${id}`, {
      method: 'PUT',
      body: JSON.stringify(projectData),
    });
  },

  // âœ… THIS DELETE FUNCTION IS PROBABLY MISSING!
  deleteProject: async (id) => {
    return await apiCall(`/projects/admin/delete/${id}`, {
      method: 'DELETE',
    });
  },
};

export default apiCall;
