import React, { useState, useEffect } from 'react';
import { blogAPI, projectAPI, mockData } from '../services/api';
import DashboardStats from '../components/DashboardStats';
import RecentActivity from '../components/RecentActivity';
import QuickActions from '../components/QuickActions';
import AnalyticsChart from '../components/AnalyticsChart';

const DashboardPage = () => {
    const [stats, setStats] = useState({
        blogs: { total: 0, published: 0, draft: 0 },
        projects: { total: 0, published: 0, draft: 0 }
    });
    const [recentBlogs, setRecentBlogs] = useState([]);
    const [recentProjects, setRecentProjects] = useState([]);
    const [analytics] = useState({
        views: 12540,
        visitors: 8421,
        engagement: 68
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            let blogs = [];
            let projects = [];

            try {
                const [blogsResponse, projectsResponse] = await Promise.all([
                    blogAPI.getBlogs(),
                    projectAPI.getProjects()
                ]);
                blogs = blogsResponse.data || [];
                projects = projectsResponse.data || [];
            } catch (error) {
                console.log('Using mock data for development');
                blogs = mockData.blogs;
                projects = mockData.projects;
            }

            const blogStats = {
                total: blogs.length,
                published: blogs.filter(blog => blog.status === 'published').length,
                draft: blogs.filter(blog => blog.status === 'draft').length
            };

            const projectStats = {
                total: projects.length,
                published: projects.filter(project => project.status === 'published').length,
                draft: projects.filter(project => project.status === 'draft').length
            };

            setStats({
                blogs: blogStats,
                projects: projectStats
            });

            setRecentBlogs(blogs.slice(0, 5));
            setRecentProjects(projects.slice(0, 5));

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="dashboard-loading">
                <div className="loading-spinner">
                    <div className="neo-spinner"></div>
                    Loading Dashboard...
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-page">
            {/* Animated Background */}
            <div className="dashboard-background"></div>
            
            {/* Floating Elements */}
            <div className="floating-elements">
                <div className="floating-element"></div>
                <div className="floating-element"></div>
                <div className="floating-element"></div>
            </div>

            {/* Header */}
            <div className="dashboard-header">
                <div className="page-header">
                    <h1>Dashboard Overview</h1>
                    <p>Welcome back! Here's what's happening with your content today.</p>
                </div>
            </div>

            {/* Main Content */}
            <div className="dashboard-content">
                {/* Stats Section */}
                <DashboardStats stats={stats} analytics={analytics} />
                
                {/* Charts and Analytics */}
                <div className="dashboard-grid">
                    <div className="dashboard-column">
                        <AnalyticsChart />
                        <QuickActions />
                    </div>
                    <div className="dashboard-column">
                        <RecentActivity 
                            blogs={recentBlogs} 
                            projects={recentProjects} 
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
