import React, { useState, useEffect, useCallback } from 'react';
import { adminApi } from '../services/api';
import './BlogManager.css';

const BlogManager = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    excerpt: '',
    tags: '',
    published: false,
    featuredImage: null
  });

  // Wrap fetchBlogs in useCallback to memoize it
  const fetchBlogs = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      const result = await adminApi.getBlogs(currentPage, 10);
      
      // Handle different response structures
      const blogsData = result.blogs || result || [];
      const totalPagesData = result.totalPages || 1;
      
      setBlogs(blogsData);
      setTotalPages(totalPagesData);
    } catch (error) {
      console.error('Error fetching blogs:', error);
      setError('Failed to load blogs. Please check your connection.');
      setBlogs([]); // Ensure blogs is always an array
    } finally {
      setLoading(false);
    }
  }, [currentPage]);

  useEffect(() => {
    fetchBlogs();
  }, [fetchBlogs]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const tagsArray = formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag);
      
      const blogData = {
        ...formData,
        tags: tagsArray
      };

      if (editingBlog) {
        await adminApi.updateBlog(editingBlog._id, blogData);
      } else {
        await adminApi.createBlog(blogData);
      }
      
      setShowForm(false);
      setEditingBlog(null);
      setFormData({ 
        title: '', 
        content: '', 
        excerpt: '', 
        tags: '', 
        published: false, 
        featuredImage: null 
      });
      fetchBlogs();
    } catch (error) {
      console.error('Error saving blog:', error);
      alert('Error saving blog: ' + (error.message || 'Unknown error'));
    }
  };

  const handleEdit = (blog) => {
    setEditingBlog(blog);
    setFormData({
      title: blog.title,
      content: blog.content,
      excerpt: blog.excerpt,
      tags: blog.tags?.join(', ') || '',
      published: blog.published || false,
      featuredImage: blog.featuredImage
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await adminApi.deleteBlog(id);
        fetchBlogs();
      } catch (error) {
        console.error('Error deleting blog:', error);
        alert('Error deleting blog: ' + (error.message || 'Unknown error'));
      }
    }
  };

  const handleImageChange = (e) => {
    setFormData({
      ...formData,
      featuredImage: e.target.files[0]
    });
  };

  if (loading) {
    return (
      <div className="blog-manager">
        <div className="manager-header">
          <h2>Blog Management</h2>
          <button className="btn-primary" disabled>
            + Add New Blog
          </button>
        </div>
        <div className="loading">Loading blogs...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="blog-manager">
        <div className="manager-header">
          <h2>Blog Management</h2>
          <button className="btn-primary" disabled>
            + Add New Blog
          </button>
        </div>
        <div className="error-message">
          <p>{error}</p>
          <button onClick={fetchBlogs} className="retry-btn">
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="blog-manager">
      <div className="manager-header">
        <h2>Blog Management</h2>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(true)}
        >
          + Add New Blog
        </button>
      </div>

      {showForm && (
        <div className="form-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingBlog ? 'Edit Blog' : 'Create New Blog'}</h3>
              <button 
                className="close-btn"
                onClick={() => {
                  setShowForm(false);
                  setEditingBlog(null);
                  setFormData({ 
                    title: '', 
                    content: '', 
                    excerpt: '', 
                    tags: '', 
                    published: false, 
                    featuredImage: null 
                  });
                }}
              >
                Ã—
              </button>
            </div>
            
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Title *</label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => setFormData({...formData, title: e.target.value})}
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Excerpt *</label>
                <textarea
                  value={formData.excerpt}
                  onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
                  rows="3"
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Content *</label>
                <textarea
                  value={formData.content}
                  onChange={(e) => setFormData({...formData, content: e.target.value})}
                  rows="10"
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Tags (comma separated)</label>
                <input
                  type="text"
                  value={formData.tags}
                  onChange={(e) => setFormData({...formData, tags: e.target.value})}
                  placeholder="web development, javascript, react"
                />
              </div>

              <div className="form-group">
                <label>Featured Image</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                />
                {formData.featuredImage && typeof formData.featuredImage === 'string' && (
                  <div className="current-image">
                    <p>Current Image:</p>
                    <img 
                      src={`${process.env.REACT_APP_API_URL}${formData.featuredImage}`} 
                      alt="Current featured" 
                      style={{maxWidth: '200px', maxHeight: '150px'}}
                    />
                  </div>
                )}
              </div>
              
              <div className="form-group checkbox-group">
                <label>
                  <input
                    type="checkbox"
                    checked={formData.published}
                    onChange={(e) => setFormData({...formData, published: e.target.checked})}
                  />
                  Published (visible on frontend)
                </label>
              </div>

              <div className="form-actions">
                <button type="submit" className="btn-primary">
                  {editingBlog ? 'Update' : 'Create'} Blog
                </button>
                <button 
                  type="button" 
                  className="btn-secondary"
                  onClick={() => {
                    setShowForm(false);
                    setEditingBlog(null);
                    setFormData({ 
                      title: '', 
                      content: '', 
                      excerpt: '', 
                      tags: '', 
                      published: false, 
                      featuredImage: null 
                    });
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="blogs-list">
        {/* Safely check if blogs is an array before mapping */}
        {Array.isArray(blogs) && blogs.length > 0 ? (
          blogs.map(blog => (
            <div key={blog._id} className="blog-item">
              <div className="blog-info">
                <h4>{blog.title || 'Untitled Blog'}</h4>
                <p className="blog-excerpt">{blog.excerpt || 'No excerpt available'}</p>
                <div className="blog-meta">
                  <span>Slug: {blog.slug || 'no-slug'}</span>
                  <span>Created: {blog.createdAt ? new Date(blog.createdAt).toLocaleDateString() : 'Unknown date'}</span>
                  <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                    {blog.published ? 'Published' : 'Draft'}
                  </span>
                </div>
                {blog.tags && blog.tags.length > 0 && (
                  <div className="blog-tags">
                    {blog.tags.map((tag, index) => (
                      <span key={index} className="tag">{tag}</span>
                    ))}
                  </div>
                )}
              </div>
              
              <div className="blog-actions">
                <button 
                  className="btn-edit"
                  onClick={() => handleEdit(blog)}
                >
                  Edit
                </button>
                <button 
                  className="btn-delete"
                  onClick={() => handleDelete(blog._id)}
                >
                  Delete
                </button>
                {blog.slug && (
                  <a 
                    href={`/blog/${blog.slug}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="btn-preview"
                  >
                    Preview
                  </a>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="no-data">
            <p>No blogs found. Create your first blog post!</p>
          </div>
        )}
      </div>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="pagination">
          <button 
            disabled={currentPage === 1}
            onClick={() => setCurrentPage(currentPage - 1)}
            className="pagination-btn"
          >
            Previous
          </button>
          <span className="pagination-info">Page {currentPage} of {totalPages}</span>
          <button 
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage(currentPage + 1)}
            className="pagination-btn"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
};

export default BlogManager;
