import React, { useState, useEffect } from 'react';
import { adminApi, blogAPI } from '../services/api';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBlogs: 0,
    publishedBlogs: 0,
    draftBlogs: 0,
    totalViews: 0
  });
  const [recentBlogs, setRecentBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch blog statistics
      const blogsResponse = await blogAPI.getBlogs();
      const blogs = blogsResponse.data;
      
      // Calculate stats from blogs data
      const totalBlogs = blogs.length;
      const publishedBlogs = blogs.filter(blog => blog.status === 'published').length;
      const draftBlogs = blogs.filter(blog => blog.status === 'draft').length;
      const totalViews = blogs.reduce((sum, blog) => sum + (blog.views || 0), 0);
      
      setStats({
        totalBlogs,
        publishedBlogs,
        draftBlogs,
        totalViews
      });
      
      // Get recent blogs (last 5)
      setRecentBlogs(blogs.slice(0, 5));
      
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Dashboard</h1>
      
      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-500">
          <h3 className="text-lg font-semibold text-gray-600">Total Blogs</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.totalBlogs}</p>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-green-500">
          <h3 className="text-lg font-semibold text-gray-600">Published</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.publishedBlogs}</p>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-yellow-500">
          <h3 className="text-lg font-semibold text-gray-600">Drafts</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.draftBlogs}</p>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-purple-500">
          <h3 className="text-lg font-semibold text-gray-600">Total Views</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.totalViews}</p>
        </div>
      </div>

      {/* Recent Blogs */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-4">Recent Blog Posts</h2>
        
        {recentBlogs.length > 0 ? (
          <div className="space-y-4">
            {recentBlogs.map(blog => (
              <div key={blog._id} className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                <div>
                  <h3 className="font-semibold text-gray-800">{blog.title}</h3>
                  <p className="text-sm text-gray-600">
                    {blog.status === 'published' ? (
                      <span className="text-green-600">Published</span>
                    ) : (
                      <span className="text-yellow-600">Draft</span>
                    )}
                    {' • '}
                    {new Date(blog.createdAt).toLocaleDateString()}
                    {' • '}
                    {blog.views || 0} views
                  </p>
                </div>
                <div className="flex space-x-2">
                  <button className="px-3 py-1 bg-blue-100 text-blue-700 rounded text-sm hover:bg-blue-200">
                    Edit
                  </button>
                  <button className="px-3 py-1 bg-red-100 text-red-700 rounded text-sm hover:bg-red-200">
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-gray-500">
            No blog posts yet. Create your first blog post!
          </div>
        )}
        
        <div className="mt-6">
          <button 
            onClick={() => window.location.href = '/admin/blogs'}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            Manage All Blogs
          </button>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="mt-8 bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-4">Quick Actions</h2>
        <div className="flex space-x-4">
          <button 
            onClick={() => window.location.href = '/admin/blogs/new'}
            className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700"
          >
            + New Blog Post
          </button>
          <button 
            onClick={() => window.location.href = '/admin/projects'}
            className="bg-purple-600 text-white px-6 py-3 rounded-lg hover:bg-purple-700"
          >
            Manage Projects
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
