import React from 'react';

const DashboardStats = ({ stats, analytics }) => {
    const statCards = [
        {
            icon: 'üìù',
            title: 'Total Blogs',
            value: stats.blogs.total,
            change: '+12%',
            trend: 'up',
            color: 'var(--primary-color)',
            details: [
                { label: 'Published', value: stats.blogs.published, color: '#10b981' },
                { label: 'Draft', value: stats.blogs.draft, color: '#f59e0b' }
            ]
        },
        {
            icon: 'üíº',
            title: 'Total Projects',
            value: stats.projects.total,
            change: '+8%',
            trend: 'up',
            color: '#8b5cf6',
            details: [
                { label: 'Published', value: stats.projects.published, color: '#10b981' },
                { label: 'Draft', value: stats.projects.draft, color: '#f59e0b' }
            ]
        },
        {
            icon: 'üëÅÔ∏è',
            title: 'Total Views',
            value: analytics.views.toLocaleString(),
            change: '+23%',
            trend: 'up',
            color: '#06b6d4',
            details: [
                { label: 'This Month', value: '2.4K', color: '#06b6d4' }
            ]
        },
        {
            icon: 'üöÄ',
            title: 'Engagement Rate',
            value: `${analytics.engagement}%`,
            change: '+5%',
            trend: 'up',
            color: '#10b981',
            details: [
                { label: 'Avg. Time', value: '3:45', color: '#10b981' }
            ]
        }
    ];

    return (
        <div className="dashboard-stats">
            <div className="stats-grid">
                {statCards.map((stat, index) => (
                    <div key={index} className="stat-card">
                        <div className="stat-header">
                            <div 
                                className="stat-icon"
                                style={{ backgroundColor: `${stat.color}15`, color: stat.color }}
                            >
                                {stat.icon}
                            </div>
                            <div className="stat-trend">
                                <span className={`trend ${stat.trend}`}>
                                    {stat.change}
                                </span>
                            </div>
                        </div>
                        
                        <div className="stat-content">
                            <h3 className="stat-title">{stat.title}</h3>
                            <div className="stat-value">{stat.value}</div>
                            
                            <div className="stat-details">
                                {stat.details.map((detail, idx) => (
                                    <div key={idx} className="detail-item">
                                        <div className="detail-dot" style={{ backgroundColor: detail.color }}></div>
                                        <span className="detail-label">{detail.label}:</span>
                                        <span className="detail-value">{detail.value}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                        
                        <div className="stat-chart">
                            <div className="mini-chart">
                                {[30, 45, 60, 75, 65, 80, 90].map((height, idx) => (
                                    <div 
                                        key={idx} 
                                        className="chart-bar"
                                        style={{ 
                                            height: `${height}%`,
                                            backgroundColor: stat.color
                                        }}
                                    ></div>
                                ))}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DashboardStats;
