import React, { useState, useEffect } from 'react';
import { projectsAPI } from '../services/api';
import './ProjectManager.css';

const ProjectManager = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    technologies: '',
    projectUrl: '',
    githubUrl: '',
    status: 'active',
    featured: false
  });

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const response = await projectsAPI.getProjects();
      // Handle different response formats
      const projectsData = Array.isArray(response) ? response : 
                          response.data || response.projects || [];
      setProjects(projectsData);
      setError('');
    } catch (err) {
      setError('Failed to load projects');
      console.error('Fetch projects error:', err);
      // Fallback demo data
      setProjects([
        { id: 1, title: 'E-commerce Website', status: 'active', technologies: 'React, Node.js', createdAt: new Date().toISOString() },
        { id: 2, title: 'Mobile App', status: 'completed', technologies: 'React Native, Firebase', createdAt: new Date().toISOString() }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (e) => {
    e.preventDefault();
    try {
      await projectsAPI.createProject(formData);
      setShowCreateForm(false);
      setFormData({ 
        title: '', 
        description: '', 
        technologies: '', 
        projectUrl: '', 
        githubUrl: '', 
        status: 'active', 
        featured: false 
      });
      fetchProjects(); // Refresh the list
      alert('Project created successfully!');
    } catch (err) {
      alert('Failed to create project: ' + err.message);
    }
  };

  const handleDeleteProject = async (id) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        await projectsAPI.deleteProject(id);
        fetchProjects(); // Refresh the list
        alert('Project deleted successfully!');
      } catch (err) {
        alert('Failed to delete project: ' + err.message);
      }
    }
  };

  if (loading) {
    return (
      <div className="project-manager">
        <div className="loading">Loading projects...</div>
      </div>
    );
  }

  return (
    <div className="project-manager">
      <div className="page-header">
        <h1>Project Management</h1>
        <button 
          className="btn-primary"
          onClick={() => setShowCreateForm(!showCreateForm)}
        >
          {showCreateForm ? '‚úñ Cancel' : '‚ûï Add New Project'}
        </button>
      </div>

      {error && (
        <div className="error-message">
          {error} - Showing demo data
        </div>
      )}

      {/* Create Project Form */}
      {showCreateForm && (
        <div className="create-form">
          <h2>Create New Project</h2>
          <form onSubmit={handleCreateProject}>
            <div className="form-group">
              <label>Title</label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({...formData, title: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                rows="4"
                required
              />
            </div>
            <div className="form-group">
              <label>Technologies</label>
              <input
                type="text"
                value={formData.technologies}
                onChange={(e) => setFormData({...formData, technologies: e.target.value})}
                placeholder="React, Node.js, MongoDB"
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>Project URL</label>
                <input
                  type="url"
                  value={formData.projectUrl}
                  onChange={(e) => setFormData({...formData, projectUrl: e.target.value})}
                  placeholder="https://example.com"
                />
              </div>
              <div className="form-group">
                <label>GitHub URL</label>
                <input
                  type="url"
                  value={formData.githubUrl}
                  onChange={(e) => setFormData({...formData, githubUrl: e.target.value})}
                  placeholder="https://github.com/username/repo"
                />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>Status</label>
                <select
                  value={formData.status}
                  onChange={(e) => setFormData({...formData, status: e.target.value})}
                >
                  <option value="active">Active</option>
                  <option value="completed">Completed</option>
                  <option value="archived">Archived</option>
                </select>
              </div>
              <div className="form-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={formData.featured}
                    onChange={(e) => setFormData({...formData, featured: e.target.checked})}
                  />
                  Featured Project
                </label>
              </div>
            </div>
            <div className="form-actions">
              <button type="submit" className="btn-primary">Create Project</button>
              <button 
                type="button" 
                className="btn-secondary"
                onClick={() => setShowCreateForm(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Projects List */}
      <div className="projects-list">
        <h2>All Projects ({projects.length})</h2>
        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Technologies</th>
                <th>Status</th>
                <th>Featured</th>
                <th>Created</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {projects.map(project => (
                <tr key={project.id}>
                  <td className="project-title">{project.title}</td>
                  <td>{project.technologies}</td>
                  <td>
                    <span className={`status-badge ${project.status}`}>
                      {project.status}
                    </span>
                  </td>
                  <td>
                    {project.featured ? '‚≠ê' : '‚Äî'}
                  </td>
                  <td>{new Date(project.createdAt).toLocaleDateString()}</td>
                  <td>
                    <div className="action-buttons">
                      <button className="btn-edit">‚úèÔ∏è Edit</button>
                      <button 
                        className="btn-delete"
                        onClick={() => handleDeleteProject(project.id)}
                      >
                        üóëÔ∏è Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProjectManager;
