// services/api.js

// ... your existing API code ...

// Mock API for development/demo purposes
export const mockBlogAPI = {
  getAll: async () => {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    return [
      {
        id: 1,
        title: 'Getting Started with React',
        content: 'Learn the basics of React development...',
        author: 'Admin User',
        published: true,
        createdAt: '2024-01-15',
        category: 'React',
        tags: ['react', 'javascript', 'frontend']
      },
      {
        id: 2,
        title: 'Advanced Node.js Patterns',
        content: 'Deep dive into Node.js architecture...',
        author: 'Admin User',
        published: false,
        createdAt: '2024-01-10',
        category: 'Node.js',
        tags: ['nodejs', 'backend', 'javascript']
      },
      {
        id: 3,
        title: 'CSS Grid vs Flexbox',
        content: 'When to use Grid and when to use Flexbox...',
        author: 'Admin User',
        published: true,
        createdAt: '2024-01-05',
        category: 'CSS',
        tags: ['css', 'frontend', 'layout']
      }
    ];
  },
  
  getById: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    
    const blogs = await mockBlogAPI.getAll();
    return blogs.find(blog => blog.id === parseInt(id)) || null;
  },
  
  create: async (blogData) => {
    await new Promise(resolve => setTimeout(resolve, 800));
    
    const newBlog = {
      id: Date.now(),
      ...blogData,
      createdAt: new Date().toISOString().split('T')[0],
      author: 'Admin User'
    };
    
    return newBlog;
  },
  
  update: async (id, blogData) => {
    await new Promise(resolve => setTimeout(resolve, 600));
    
    return {
      id: parseInt(id),
      ...blogData,
      updatedAt: new Date().toISOString()
    };
  },
  
  delete: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 400));
    
    return { success: true, message: 'Blog deleted successfully' };
  }
};

