// admin-panel/src/components/auth/Login.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';
import './Login.css';

function Login() {
  const [email, setEmail] = useState('admin@example.com');
  const [password, setPassword] = useState('password');
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const { login, user, error, clearError, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  console.log('üîê Login component - User:', user, 'isAuthenticated:', isAuthenticated);

  // Redirect if already logged in
  useEffect(() => {
    console.log('üîÑ Login useEffect - user changed:', user);
    if (user) {
      console.log('‚úÖ User authenticated, redirecting...');
      const from = location.state?.from?.pathname || '/dashboard';
      navigate(from, { replace: true });
    }
  }, [user, navigate, location]);

  useEffect(() => {
    clearError();
  }, [email, password, clearError]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!email || !password) {
      return;
    }

    console.log('üìù Form submitted:', { email, password });
    setIsSubmitting(true);
    
    const result = await login(email, password);
    console.log('üì® Login result:', result);
    
    setIsSubmitting(false);

    if (result.success) {
      console.log('üéâ Login successful in handleSubmit');
      // The redirect will be handled by the useEffect above
    } else {
      console.log('‚ùå Login failed in handleSubmit');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h1>Admin Login</h1>
          <p>Access your dashboard</p>
          <div style={{fontSize: '12px', color: '#666', marginTop: '10px'}}>
            Debug: User: {user ? 'Yes' : 'No'}, Auth: {isAuthenticated ? 'Yes' : 'No'}
          </div>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
              disabled={isSubmitting}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
              disabled={isSubmitting}
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary login-btn"
            disabled={isSubmitting || !email || !password}
          >
            {isSubmitting ? 'Signing in...' : 'Sign In'}
          </button>
        </form>

        <div className="login-demo">
          <h3>Demo Credentials</h3>
          <p><strong>Email:</strong> admin@example.com</p>
          <p><strong>Password:</strong> password</p>
        </div>
      </div>
    </div>
  );
}

export default Login;
