import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './BlogManager.css';

const BlogManager = ({ blogs, loading, onCreate, onUpdate, onDelete }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    excerpt: '',
    content: '',
    tags: '',
    published: false,
  });

  const modules = {
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      ['link', 'image'],
      ['clean']
    ],
  };

  const resetForm = () => {
    setFormData({
      title: '',
      excerpt: '',
      content: '',
      tags: '',
      published: false,
    });
    setEditingBlog(null);
    setShowForm(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const blogData = {
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        slug: formData.title.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/(^-|-$)+/g, ''),
      };

      if (editingBlog) {
        await onUpdate(editingBlog._id, blogData);
      } else {
        await onCreate(blogData);
      }
      resetForm();
    } catch (error) {
      console.error('Error saving blog:', error);
    }
  };

  const handleEdit = (blog) => {
    setFormData({
      title: blog.title,
      excerpt: blog.excerpt,
      content: blog.content,
      tags: blog.tags.join(', '),
      published: blog.published,
    });
    setEditingBlog(blog);
    setShowForm(true);
  };

  if (loading) {
    return <div className="loading">Loading blogs...</div>;
  }

  return (
    <div className="blog-manager">
      <div className="manager-header">
        <h2>Blog Posts</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(true)}
        >
          Add New Blog
        </button>
      </div>

      {showForm && (
        <div className="blog-form-overlay">
          <div className="blog-form">
            <h3>{editingBlog ? 'Edit Blog' : 'Create New Blog'}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Title</label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  required
                />
              </div>

              <div className="form-group">
                <label>Excerpt</label>
                <textarea
                  value={formData.excerpt}
                  onChange={(e) => setFormData({ ...formData, excerpt: e.target.value })}
                  required
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label>Content</label>
                <ReactQuill
                  value={formData.content}
                  onChange={(content) => setFormData({ ...formData, content })}
                  modules={modules}
                  theme="snow"
                />
              </div>

              <div className="form-group">
                <label>Tags (comma separated)</label>
                <input
                  type="text"
                  value={formData.tags}
                  onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                />
              </div>

              <div className="form-group checkbox">
                <label>
                  <input
                    type="checkbox"
                    checked={formData.published}
                    onChange={(e) => setFormData({ ...formData, published: e.target.checked })}
                  />
                  Published
                </label>
              </div>

              <div className="form-actions">
                <button type="submit" className="btn btn-primary">
                  {editingBlog ? 'Update' : 'Create'}
                </button>
                <button type="button" className="btn btn-secondary" onClick={resetForm}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="blogs-list">
        {blogs.map(blog => (
          <div key={blog._id} className="blog-item">
            <div className="blog-info">
              <h4>{blog.title}</h4>
              <p className="blog-excerpt">{blog.excerpt}</p>
              <div className="blog-meta">
                <span>Status: {blog.published ? 'Published' : 'Draft'}</span>
                <span>Tags: {blog.tags.join(', ')}</span>
                <span>Created: {new Date(blog.createdAt).toLocaleDateString()}</span>
              </div>
            </div>
            <div className="blog-actions">
              <button 
                className="btn btn-outline"
                onClick={() => handleEdit(blog)}
              >
                Edit
              </button>
              <button 
                className="btn btn-danger"
                onClick={() => onDelete(blog._id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}

        {blogs.length === 0 && (
          <div className="no-items">
            <p>No blog posts found. Create your first blog post!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default BlogManager;
