// admin-panel/src/services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
console.log('ðŸ”§ API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
  timeout: 10000, // 10 second timeout
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  console.log(`ðŸš€ ${config.method?.toUpperCase()} Request to: ${config.url}`);
  return config;
});

// Handle responses globally
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… ${response.status} Response from: ${response.config.url}`, response.data);
    return response;
  },
  (error) => {
    console.error(`âŒ API Error:`, {
      url: error.config?.url,
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    
    if (error.response?.status === 401) {
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// --- Authentication API ---
export const authAPI = {
  login: async (email, password) => {
    const res = await api.post('/auth/login', { email, password });
    return res.data; // Return only data
  },
  logout: async () => {
    const res = await api.post('/auth/logout');
    return res.data; // Return only data
  },
  verify: async () => {
    const res = await api.get('/auth/verify');
    return res.data; // Return only data
  },
};

// --- Dashboard API ---
export const dashboardAPI = {
  getStats: async () => {
    const res = await api.get('/admin/dashboard/stats');
    return res.data; // Return only data
  },
  getRecentActivity: async () => {
    const res = await api.get('/admin/dashboard/recent');
    return res.data; // Return only data
  },
  createTestBlog: async () => {
    const res = await api.post('/admin/blogs/test');
    return res.data; // Return only data
  },
  createTestProject: async () => {
    const res = await api.post('/admin/projects/test');
    return res.data; // Return only data
  },
  publishBlog: async (id) => {
    const res = await api.post(`/admin/blogs/${id}/publish`);
    return res.data; // Return only data
  },
};

// --- Blog API ---
export const blogAPI = {
  getAll: async (page = 1, limit = 10) => {
    const res = await api.get(`/admin/blogs?page=${page}&limit=${limit}`);
    
    // Handle different response structures
    const responseData = res.data;
    
    // If response has a data property (common in many APIs)
    if (responseData && Array.isArray(responseData.data)) {
      console.log('ðŸ“š Blogs data (from response.data.data):', responseData.data);
      return responseData.data;
    }
    
    // If response is directly the array
    if (Array.isArray(responseData)) {
      console.log('ðŸ“š Blogs data (direct array):', responseData);
      return responseData;
    }
    
    // If response has blogs property
    if (responseData && Array.isArray(responseData.blogs)) {
      console.log('ðŸ“š Blogs data (from response.data.blogs):', responseData.blogs);
      return responseData.blogs;
    }
    
    // If no array found, return empty array
    console.warn('âš ï¸ No blog array found in response, returning empty array');
    return [];
  },
  
  create: async (data) => {
    const res = await api.post('/admin/blogs', data);
    return res.data; // Return only data
  },
  
  update: async (id, data) => {
    const res = await api.put(`/admin/blogs/${id}`, data);
    return res.data; // Return only data
  },
  
  delete: async (id) => {
    const res = await api.delete(`/admin/blogs/${id}`);
    return res.data; // Return only data
  },
  
  // Get single blog by ID
  getById: async (id) => {
    const res = await api.get(`/admin/blogs/${id}`);
    return res.data; // Return only data
  },
};

// --- Project API ---
export const projectAPI = {
  getAll: async (page = 1, limit = 10) => {
    const res = await api.get(`/admin/projects?page=${page}&limit=${limit}`);
    
    // Handle different response structures
    const responseData = res.data;
    
    if (responseData && Array.isArray(responseData.data)) {
      return responseData.data;
    }
    
    if (Array.isArray(responseData)) {
      return responseData;
    }
    
    if (responseData && Array.isArray(responseData.projects)) {
      return responseData.projects;
    }
    
    console.warn('âš ï¸ No project array found in response, returning empty array');
    return [];
  },
  
  create: async (data) => {
    const res = await api.post('/admin/projects', data);
    return res.data; // Return only data
  },
  
  update: async (id, data) => {
    const res = await api.put(`/admin/projects/${id}`, data);
    return res.data; // Return only data
  },
  
  delete: async (id) => {
    const res = await api.delete(`/admin/projects/${id}`);
    return res.data; // Return only data
  },
};

// --- Contact/Message API ---
export const contactAPI = {
  getAll: async (page = 1, limit = 10) => {
    const res = await api.get(`/admin/contacts?page=${page}&limit=${limit}`);
    
    const responseData = res.data;
    
    if (responseData && Array.isArray(responseData.data)) {
      return responseData.data;
    }
    
    if (Array.isArray(responseData)) {
      return responseData;
    }
    
    if (responseData && Array.isArray(responseData.contacts)) {
      return responseData.contacts;
    }
    
    console.warn('âš ï¸ No contacts array found in response, returning empty array');
    return [];
  },
  
  getById: async (id) => {
    const res = await api.get(`/admin/contacts/${id}`);
    return res.data;
  },
  
  updateStatus: async (id, status) => {
    const res = await api.patch(`/admin/contacts/${id}`, { status });
    return res.data;
  },
  
  delete: async (id) => {
    const res = await api.delete(`/admin/contacts/${id}`);
    return res.data;
  },
};

// --- File Upload API ---
export const uploadAPI = {
  uploadImage: async (file) => {
    const formData = new FormData();
    formData.append('image', file);
    
    const res = await api.post('/admin/upload/image', formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return res.data;
  },
  
  uploadFile: async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    
    const res = await api.post('/admin/upload/file', formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return res.data;
  },
};

// Utility function to check API connectivity
export const checkAPIHealth = async () => {
  try {
    const res = await api.get('/health');
    return res.data;
  } catch (error) {
    console.error('API Health check failed:', error);
    throw error;
  }
};

export default api;
