import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  logout: () => api.post('/auth/logout'),
  checkAuth: () => api.get('/auth/me'),
};

export const blogAPI = {
  // Get all blogs
  getBlogs: () => api.get('/blogs'),
  // Get single blog
  getBlog: (id) => api.get(`/blogs/${id}`),
  // Create blog
  createBlog: (blogData) => api.post('/blogs', blogData),
  // Update blog
  updateBlog: (id, blogData) => api.put(`/blogs/${id}`, blogData),
  // Delete blog
  deleteBlog: (id) => api.delete(`/blogs/${id}`),
  // Upload image
  uploadImage: (imageData) => api.post('/blogs/upload', imageData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
  // Get blog stats for dashboard
  getStats: () => api.get('/blogs/stats'),
};

export const projectAPI = {
  // Get all projects
  getProjects: () => api.get('/projects'),
  // Get single project
  getProject: (id) => api.get(`/projects/${id}`),
  // Create project
  createProject: (projectData) => api.post('/projects', projectData),
  // Update project
  updateProject: (id, projectData) => api.put(`/projects/${id}`, projectData),
  // Delete project
  deleteProject: (id) => api.delete(`/projects/${id}`),
  // Upload project image
  uploadImage: (imageData) => api.post('/projects/upload', imageData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
  // Get project stats for dashboard
  getStats: () => api.get('/projects/stats'),
};

export default api;
