// admin-panel/src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import './Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    totalBlogs: 0,
    totalProjects: 0,
    totalContacts: 0,
    publishedBlogs: 0,
    draftBlogs: 0,
    totalViews: 0
  });
  const [apiStatus, setApiStatus] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Starting data fetch...\n');
      
      const token = localStorage.getItem('adminToken');
      console.log('üîê Token exists:', !!token);
      
      if (!token) {
        throw new Error('Please login to admin panel first');
      }

      // Test multiple API endpoints
      const endpoints = [
        {
          name: 'Dashboard Stats',
          url: 'http://localhost:5000/api/admin/dashboard/stats',
          requiresAuth: true
        },
        {
          name: 'Blogs API',
          url: 'http://localhost:5000/api/blogs',
          requiresAuth: false
        },
        {
          name: 'Projects API', 
          url: 'http://localhost:5000/api/projects',
          requiresAuth: false
        },
        {
          name: 'Contacts API',
          url: 'http://localhost:5000/api/contacts',
          requiresAuth: true
        },
        {
          name: 'Debug API',
          url: 'http://localhost:5000/api/admin/debug/database',
          requiresAuth: true
        }
      ];

      const results = {};
      let debugLog = 'API Test Results:\n\n';

      for (const endpoint of endpoints) {
        try {
          debugLog += `üîç Testing ${endpoint.name}...\n`;
          debugLog += `   URL: ${endpoint.url}\n`;
          
          const headers = {};
          if (endpoint.requiresAuth) {
            headers['Authorization'] = `Bearer ${token}`;
          }

          const response = await fetch(endpoint.url, { headers });
          debugLog += `   Status: ${response.status} ${response.statusText}\n`;

          if (response.ok) {
            const data = await response.json();
            results[endpoint.name] = {
              success: true,
              data: data,
              count: Array.isArray(data) ? data.length : 'N/A (not array)'
            };
            debugLog += `   ‚úÖ Success: ${JSON.stringify(data).substring(0, 100)}...\n\n`;
          } else {
            results[endpoint.name] = {
              success: false,
              error: `${response.status} ${response.statusText}`
            };
            debugLog += `   ‚ùå Failed: ${response.status} ${response.statusText}\n\n`;
          }
        } catch (err) {
          results[endpoint.name] = {
            success: false,
            error: err.message
          };
          debugLog += `   üí• Error: ${err.message}\n\n`;
        }
      }

      setApiStatus(results);
      setDebugInfo(debugLog);

      // Extract counts from successful APIs
      const extractedStats = extractStatsFromResults(results);
      setStats(extractedStats);

      console.log('üìä Final stats:', extractedStats);
      console.log('üîß API Status:', results);

    } catch (error) {
      console.error('‚ùå Dashboard fetch failed:', error);
      setError(`Failed to load dashboard: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const extractStatsFromResults = (results) => {
    const stats = {
      totalBlogs: 0,
      totalProjects: 0,
      totalContacts: 0,
      publishedBlogs: 0,
      draftBlogs: 0,
      totalViews: 0
    };

    // Try to get counts from Dashboard Stats first
    if (results['Dashboard Stats']?.success) {
      const dashboard = results['Dashboard Stats'].data;
      stats.totalBlogs = dashboard.totalBlogs || dashboard.blogs || 0;
      stats.totalProjects = dashboard.totalProjects || dashboard.projects || 0;
      stats.totalContacts = dashboard.totalContacts || dashboard.contacts || dashboard.messages || 0;
      stats.publishedBlogs = dashboard.publishedBlogs || 0;
      stats.draftBlogs = dashboard.draftBlogs || 0;
      stats.totalViews = dashboard.totalViews || 0;
    }

    // Fallback to direct API counts if dashboard is empty
    if (stats.totalBlogs === 0 && results['Blogs API']?.success) {
      if (Array.isArray(results['Blogs API'].data)) {
        stats.totalBlogs = results['Blogs API'].data.length;
        stats.publishedBlogs = results['Blogs API'].data.filter(blog => blog.status === 'published').length;
        stats.draftBlogs = results['Blogs API'].data.filter(blog => blog.status === 'draft').length;
        stats.totalViews = results['Blogs API'].data.reduce((sum, blog) => sum + (blog.views || 0), 0);
      }
    }

    if (stats.totalProjects === 0 && results['Projects API']?.success) {
      if (Array.isArray(results['Projects API'].data)) {
        stats.totalProjects = results['Projects API'].data.length;
      }
    }

    if (stats.totalContacts === 0 && results['Contacts API']?.success) {
      if (Array.isArray(results['Contacts API'].data)) {
        stats.totalContacts = results['Contacts API'].data.length;
      }
    }

    return stats;
  };

  const addTestData = async (type) => {
    try {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        alert('Please login first');
        return;
      }

      let testData, url;
      
      if (type === 'blog') {
        testData = {
          title: `Test Blog ${new Date().toLocaleString()}`,
          content: "This is a test blog post content",
          excerpt: "Test blog excerpt for counting verification",
          status: "published",
          tags: ["test", "debug"],
          views: 0
        };
        url = 'http://localhost:5000/api/blogs';
      } else if (type === 'project') {
        testData = {
          title: `Test Project ${new Date().toLocaleString()}`,
          description: "This is a test project",
          technologies: ["React", "Node.js"],
          githubUrl: "https://github.com/test",
          liveUrl: "https://test.com",
          featured: false
        };
        url = 'http://localhost:5000/api/projects';
      } else if (type === 'contact') {
        testData = {
          name: "Test User",
          email: "test@example.com",
          subject: "Test Message",
          message: "This is a test contact message to verify counting",
          createdAt: new Date().toISOString()
        };
        url = 'http://localhost:5000/api/contacts';
      }

      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(testData)
      });

      if (response.ok) {
        const result = await response.json();
        alert(`‚úÖ Test ${type} added successfully!`);
        console.log(`‚úÖ Test ${type} added:`, result);
        fetchDashboardData(); // Refresh data
      } else {
        const errorText = await response.text();
        throw new Error(`Server responded with ${response.status}: ${errorText}`);
      }

    } catch (error) {
      console.error(`‚ùå Add test ${type} failed:`, error);
      alert(`Failed to add test ${type}: ${error.message}`);
    }
  };

  const resetData = async () => {
    if (!window.confirm('This will reset all test data. Continue?')) return;
    
    try {
      const token = localStorage.getItem('adminToken');
      const response = await fetch('http://localhost:5000/api/admin/debug/reset-test-data', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        alert('‚úÖ Test data reset successfully!');
        fetchDashboardData();
      } else {
        throw new Error(`Reset failed: ${response.status}`);
      }
    } catch (error) {
      alert(`Reset failed: ${error.message}`);
    }
  };

  if (loading) {
    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
          <span className="loading-badge">Loading...</span>
        </div>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Fetching dashboard data from APIs...</p>
          <pre className="debug-pre">{debugInfo}</pre>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
        </div>
        <div className="error-container">
          <div className="error-icon">‚ùå</div>
          <h3>Failed to Load Dashboard</h3>
          <p>{error}</p>
          <div className="error-actions">
            <button onClick={fetchDashboardData} className="btn-primary">
              Try Again
            </button>
            <button onClick={() => window.location.reload()} className="btn-secondary">
              Reload Page
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* Header */}
      <div className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <div className="header-actions">
          <button onClick={fetchDashboardData} className="btn-primary">
            üîÑ Refresh
          </button>
          <div className="test-buttons">
            <button onClick={() => addTestData('blog')} className="btn-success">
              ‚ûï Add Test Blog
            </button>
            <button onClick={() => addTestData('project')} className="btn-success">
              ‚ûï Add Test Project
            </button>
            <button onClick={() => addTestData('contact')} className="btn-success">
              ‚ûï Add Test Message
            </button>
          </div>
          <button onClick={resetData} className="btn-warning">
            üóëÔ∏è Reset Test Data
          </button>
        </div>
      </div>

      {/* Statistics Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon project-icon">
            <i className="fas fa-briefcase"></i>
          </div>
          <div className="stat-content">
            <h2 className="stat-number">{stats.totalProjects}</h2>
            <p className="stat-label">Total Projects</p>
            {stats.totalProjects === 0 && (
              <div className="stat-warning">
                <small>No projects found</small>
              </div>
            )}
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon blog-icon">
            <i className="fas fa-blog"></i>
          </div>
          <div className="stat-content">
            <h2 className="stat-number">{stats.totalBlogs}</h2>
            <p className="stat-label">Total Blogs</p>
            <div className="stat-subdetails">
              <span className="published">{stats.publishedBlogs} published</span>
              <span className="draft">{stats.draftBlogs} draft</span>
            </div>
            {stats.totalBlogs === 0 && (
              <div className="stat-warning">
                <small>No blogs found</small>
              </div>
            )}
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon message-icon">
            <i className="fas fa-envelope"></i>
          </div>
          <div className="stat-content">
            <h2 className="stat-number">{stats.totalContacts}</h2>
            <p className="stat-label">Messages</p>
            {stats.totalContacts === 0 && (
              <div className="stat-warning">
                <small>No messages found</small>
              </div>
            )}
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon views-icon">
            <i className="fas fa-eye"></i>
          </div>
          <div className="stat-content">
            <h2 className="stat-number">{stats.totalViews}</h2>
            <p className="stat-label">Total Views</p>
            <div className="stat-trend">
              <i className="fas fa-chart-line"></i>
              All time
            </div>
          </div>
        </div>
      </div>

      {/* API Status */}
      <div className="api-status-section">
        <h3>üîå API Connection Status</h3>
        <div className="api-status-grid">
          {Object.entries(apiStatus).map(([name, status]) => (
            <div key={name} className={`api-status-card ${status.success ? 'success' : 'error'}`}>
              <h4>{name}</h4>
              <div className="api-status">
                {status.success ? (
                  <>
                    <span className="status-badge success">‚úÖ Connected</span>
                    <div className="api-data">
                      <small>
                        Data: {typeof status.count === 'number' ? 
                          `${status.count} items` : 
                          typeof status.data}
                      </small>
                    </div>
                  </>
                ) : (
                  <>
                    <span className="status-badge error">‚ùå Failed</span>
                    <div className="api-error">
                      <small>{status.error}</small>
                    </div>
                  </>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Debug Information */}
      <div className="debug-section">
        <h3>üêõ Debug Information</h3>
        <div className="debug-actions">
          <button 
            onClick={() => navigator.clipboard.writeText(debugInfo)}
            className="btn-secondary"
          >
            üìã Copy Debug Info
          </button>
        </div>
        <pre className="debug-pre">{debugInfo}</pre>
        
        <div className="troubleshooting">
          <h4>üîß Troubleshooting Steps:</h4>
          <ol>
            <li>Check if backend server is running on port 5000</li>
            <li>Verify your API endpoints return data</li>
            <li>Use "Add Test" buttons to create sample data</li>
            <li>Check browser console for detailed errors</li>
            <li>Verify authentication token is valid</li>
          </ol>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
