import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/adminAPI';
import ProjectManager from '../components/ProjectManager';
import './ProjectsPage.css';

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [error, setError] = useState('');

  const fetchProjects = async () => {
    try {
      setLoading(true);
      console.log('üîÑ Fetching projects from admin API...');
      
      const response = await adminAPI.getProjects();
      console.log('üì¶ Projects API response:', response);
      
      if (response.data.success) {
        setProjects(response.data.data);
        setError('');
      } else {
        setError(response.data.message || 'Failed to load projects');
      }
    } catch (err) {
      console.error('‚ùå Error fetching projects:', err);
      setError('Failed to load projects: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteProject = async (projectId) => {
    console.log('üóëÔ∏è Deleting project ID:', projectId);
    
    if (!projectId) {
      alert('Error: Invalid project ID');
      return;
    }

    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        const response = await adminAPI.deleteProject(projectId);
        console.log('üì¶ Delete response:', response);
        
        if (response.data.success) {
          alert('Project deleted successfully!');
          fetchProjects(); // Refresh the list
        } else {
          alert('Failed to delete project: ' + response.data.message);
        }
      } catch (error) {
        console.error('‚ùå Delete error:', error);
        alert('Error deleting project: ' + error.message);
      }
    }
  };

  const handleSaveProject = async (projectData) => {
    try {
      console.log('üíæ Saving project:', projectData);
      
      let response;
      if (projectData._id) {
        // Update existing project
        response = await adminAPI.updateProject(projectData._id, projectData);
      } else {
        // Create new project - REMOVE FRONTEND-ONLY FIELDS
        const { id, createdAt, status, ...cleanProjectData } = projectData;
        response = await adminAPI.createProject(cleanProjectData);
      }
      
      console.log('üì¶ Save response:', response);
      
      if (response.data.success) {
        alert('Project saved successfully!');
        setShowForm(false);
        setEditingProject(null);
        fetchProjects(); // Refresh list
      } else {
        alert('Failed to save project: ' + response.data.message);
      }
    } catch (error) {
      console.error('‚ùå Save error:', error);
      alert('Error saving project: ' + error.message);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  if (loading) {
    return (
      <div className="projects-page">
        <div className="loading">Loading projects...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="projects-page">
        <div className="error-message">
          <h3>Error Loading Projects</h3>
          <p>{error}</p>
          <button onClick={fetchProjects} className="retry-btn">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="projects-page">
      <div className="page-header">
        <h1>Project Management</h1>
        <button 
          onClick={() => setShowForm(true)}
          className="btn-primary"
        >
          + Add New Project
        </button>
      </div>

      {showForm && (
        <ProjectManager
          project={editingProject}
          onSave={handleSaveProject}
          onCancel={() => {
            setShowForm(false);
            setEditingProject(null);
          }}
        />
      )}

      <div className="projects-stats">
        <p>Total Projects: {projects.length}</p>
      </div>

      {projects.length === 0 ? (
        <div className="no-projects">
          <p>No projects found. Create your first project!</p>
        </div>
      ) : (
        <div className="projects-grid">
          {projects.map(project => (
            <div key={project._id} className="project-card">
              <div className="project-image">
                {project.image ? (
                  <img src={project.image} alt={project.name} />
                ) : (
                  <div className="no-image">No Image</div>
                )}
              </div>
              
              <div className="project-content">
                <h3>{project.name}</h3>
                <p className="project-description">
                  {project.description}
                </p>
                
                {project.technologies && project.technologies.length > 0 && (
                  <div className="technologies">
                    <div className="tech-tags">
                      {project.technologies.map((tech, index) => (
                        <span key={index} className="tech-tag">{tech}</span>
                      ))}
                    </div>
                  </div>
                )}
                
                <div className="project-meta">
                  <small className="project-id">ID: {project._id}</small>
                  {project.featured && <span className="featured-badge">‚≠ê Featured</span>}
                </div>
                
                <div className="project-actions">
                  <button 
                    className="btn-edit"
                    onClick={() => {
                      setEditingProject(project);
                      setShowForm(true);
                    }}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn-delete"
                    onClick={() => {
                      console.log('üñ±Ô∏è Delete clicked for:', project._id);
                      handleDeleteProject(project._id);
                    }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProjectsPage;
