import React, { useState } from 'react';
import './ProjectManager.css';

const ProjectManager = ({ project, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: project?.name || '',
    description: project?.description || '',
    technologies: project?.technologies?.join(', ') || '',
    githubUrl: project?.githubUrl || '',
    liveUrl: project?.liveUrl || '',
    image: project?.image || '',
    featured: project?.featured || false
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // âœ… CORRECT: Prepare data for backend (remove frontend-only fields)
    const projectData = {
      name: formData.name,
      description: formData.description,
      technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
      githubUrl: formData.githubUrl,
      liveUrl: formData.liveUrl,
      image: formData.image,
      featured: formData.featured
    };
    
    // âŒ REMOVED: Frontend-only fields that backend doesn't recognize
    // id: project?.id || Date.now(),
    // createdAt: project?.createdAt || new Date().toISOString(),
    // status: formData.status,

    console.log('ðŸ“¦ Sending project data to backend:', projectData);
    onSave(projectData);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData(prev => ({ 
          ...prev, 
          image: reader.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{project ? 'Edit Project' : 'Create New Project'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Project Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              placeholder="Enter project name"
              required
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              placeholder="Enter project description"
              required
              rows="4"
            />
          </div>

          <div className="form-group">
            <label>Technologies</label>
            <input
              type="text"
              value={formData.technologies}
              onChange={(e) => setFormData({...formData, technologies: e.target.value})}
              placeholder="React, Node.js, MongoDB (comma separated)"
            />
          </div>

          <div className="form-group">
            <label>Project Image URL</label>
            <input
              type="url"
              value={formData.image}
              onChange={(e) => setFormData({...formData, image: e.target.value})}
              placeholder="https://example.com/project-image.jpg"
            />
          </div>

          <div className="form-group">
            <label>GitHub URL</label>
            <input
              type="url"
              value={formData.githubUrl}
              onChange={(e) => setFormData({...formData, githubUrl: e.target.value})}
              placeholder="https://github.com/username/project"
            />
          </div>

          <div className="form-group">
            <label>Live Demo URL</label>
            <input
              type="url"
              value={formData.liveUrl}
              onChange={(e) => setFormData({...formData, liveUrl: e.target.value})}
              placeholder="https://demo.example.com"
            />
          </div>

          <div className="form-group checkbox">
            <label>
              <input
                type="checkbox"
                checked={formData.featured}
                onChange={(e) => setFormData({...formData, featured: e.target.checked})}
              />
              Featured Project
            </label>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary">
              {project ? 'Update Project' : 'Create Project'}
            </button>
            <button type="button" className="btn-secondary" onClick={onCancel}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
