import React, { useState } from 'react';
import './ProjectManager.css';

const ProjectManager = ({ project, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: project?.name || '',
    description: project?.description || '',
    technologies: project?.technologies?.join(', ') || '',
    image: project?.image || '',
    liveUrl: project?.liveUrl || '',
    githubUrl: project?.githubUrl || '',
    demoUrl: project?.demoUrl || '',
    featured: project?.featured || false
  });

  const [imagePreview, setImagePreview] = useState(project?.image || '');
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file
      if (file.size > 5 * 1024 * 1024) {
        alert('File size must be less than 5MB');
        return;
      }
      if (!file.type.startsWith('image/')) {
        alert('Please select an image file');
        return;
      }

      setSelectedFile(file);
      const previewUrl = URL.createObjectURL(file);
      setImagePreview(previewUrl);
    }
  };

  const handleUrlChange = (e) => {
    const url = e.target.value;
    setFormData({...formData, image: url});
    setImagePreview(url);
    setSelectedFile(null);
  };

  // Upload function (same as BlogManager)
  const uploadImageToServer = async (file) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      console.log('ðŸš€ Uploading project image to server...');
      
      const response = await fetch('http://localhost:5000/api/upload/image', {
        method: 'POST',
        body: formData,
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`Upload failed with status: ${response.status}`);
      }

      const data = await response.json();
      
      if (!data.success) {
        throw new Error(data.error || 'Upload failed');
      }

      console.log('âœ… Upload successful. Image URL:', data.imageUrl);
      return data.imageUrl;

    } catch (error) {
      console.error('âŒ Upload error:', error);
      throw new Error(`Upload failed: ${error.message}`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // If no local file, save with existing URL
    if (!selectedFile) {
      const projectData = {
        name: formData.name,
        description: formData.description,
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
        image: formData.image,
        liveUrl: formData.liveUrl,
        githubUrl: formData.githubUrl,
        demoUrl: formData.demoUrl,
        featured: formData.featured
      };
      
      if (project?._id) projectData._id = project._id;
      onSave(projectData);
      return;
    }

    // If local file selected, upload it
    setUploading(true);

    try {
      console.log('ðŸ“¤ Starting project image upload...');
      const imageUrl = await uploadImageToServer(selectedFile);
      console.log('ðŸŽ‰ Project image uploaded. URL:', imageUrl);
      
      // Clean up temporary URL
      if (imagePreview.startsWith('blob:')) {
        URL.revokeObjectURL(imagePreview);
      }
      
      const projectData = {
        name: formData.name,
        description: formData.description,
        technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
        image: imageUrl,
        liveUrl: formData.liveUrl,
        githubUrl: formData.githubUrl,
        demoUrl: formData.demoUrl,
        featured: formData.featured
      };
      
      if (project?._id) projectData._id = project._id;
      
      console.log('ðŸ’¾ Saving project with image:', projectData.image);
      onSave(projectData);
      
    } catch (error) {
      alert(`Upload Error: ${error.message}`);
      console.error('Submit error:', error);
    } finally {
      setUploading(false);
    }
  };

  const clearImage = () => {
    if (imagePreview.startsWith('blob:')) {
      URL.revokeObjectURL(imagePreview);
    }
    setImagePreview('');
    setFormData({...formData, image: ''});
    setSelectedFile(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{project ? 'Edit Project' : 'Create New Project'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Project Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              required
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              rows="6"
              required
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Technologies Used (comma separated)</label>
            <input
              type="text"
              value={formData.technologies}
              onChange={(e) => setFormData({...formData, technologies: e.target.value})}
              placeholder="React, Node.js, MongoDB"
              required
              disabled={uploading}
            />
          </div>

          {/* Project URLs Section */}
          <div className="form-group">
            <label>Live Project URL</label>
            <input
              type="url"
              value={formData.liveUrl}
              onChange={(e) => setFormData({...formData, liveUrl: e.target.value})}
              placeholder="https://your-project.com"
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>GitHub Repository URL</label>
            <input
              type="url"
              value={formData.githubUrl}
              onChange={(e) => setFormData({...formData, githubUrl: e.target.value})}
              placeholder="https://github.com/username/repository"
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Demo Video URL</label>
            <input
              type="url"
              value={formData.demoUrl}
              onChange={(e) => setFormData({...formData, demoUrl: e.target.value})}
              placeholder="https://youtube.com/watch?v=..."
              disabled={uploading}
            />
          </div>

          {/* Image Section - Same as BlogManager */}
          <div className="form-group">
            <label>Project Image</label>
            
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" />
                <button type="button" className="clear-image-btn" onClick={clearImage}>
                  Remove Image
                </button>
              </div>
            )}

            <div className="image-upload-section">
              <label className="upload-label">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="file-input"
                  disabled={uploading}
                />
                <span className={`upload-button ${uploading ? 'uploading' : ''}`}>
                  {uploading ? 'Uploading...' : 'Choose Local Image'}
                </span>
              </label>
              <span className="upload-hint">or</span>
            </div>

            <input
              type="url"
              value={formData.image}
              onChange={handleUrlChange}
              placeholder="Or enter image URL"
              className="url-input"
              disabled={uploading}
            />
            
            <div className="image-notes">
              <small>Supported formats: JPG, PNG, GIF, WebP â€¢ Max size: 5MB</small>
              {selectedFile && (
                <div className="file-info">
                  <small>Selected: {selectedFile.name}</small>
                </div>
              )}
            </div>
          </div>

          <div className="form-group checkbox">
            <label>
              <input
                type="checkbox"
                checked={formData.featured}
                onChange={(e) => setFormData({...formData, featured: e.target.checked})}
                disabled={uploading}
              />
              Featured Project
            </label>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary" disabled={uploading}>
              {uploading ? 'Uploading...' : (project ? 'Update Project' : 'Create Project')}
            </button>
            <button type="button" className="btn-secondary" onClick={onCancel} disabled={uploading}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
