// admin-panel/src/services/api.js
import axios from 'axios';

// Make sure this points to your backend
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor for auth tokens if needed
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized access
      localStorage.removeItem('adminToken');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Dashboard Statistics API
export const dashboardAPI = {
  // Get all dashboard statistics
  getDashboardStats: async () => {
    try {
      const response = await api.get('/api/admin/dashboard/stats');
      return response.data;
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
      
      // If dedicated endpoint fails, try individual endpoints
      try {
        console.log('Trying individual endpoints...');
        return await getStatsFromIndividualEndpoints();
      } catch (fallbackError) {
        console.error('Fallback also failed:', fallbackError);
        throw error; // Throw original error
      }
    }
  },

  // Get total projects count
  getTotalProjects: async () => {
    try {
      const response = await api.get('/api/admin/projects');
      const projects = response.data.data || response.data;
      return Array.isArray(projects) ? projects.length : 0;
    } catch (error) {
      console.error('Error fetching projects count:', error);
      return 0;
    }
  },

  // Get total blogs count
  getTotalBlogs: async () => {
    try {
      const response = await api.get('/api/admin/blogs');
      const blogs = response.data.data || response.data;
      return Array.isArray(blogs) ? blogs.length : 0;
    } catch (error) {
      console.error('Error fetching blogs count:', error);
      return 0;
    }
  },

  // Get total messages count
  getTotalMessages: async () => {
    try {
      const response = await api.get('/api/admin/messages');
      const messages = response.data.data || response.data;
      return Array.isArray(messages) ? messages.length : 0;
    } catch (error) {
      console.error('Error fetching messages count:', error);
      return 0;
    }
  }
};

// Projects API
export const projectsAPI = {
  getProjects: async () => {
    try {
      const response = await api.get('/api/admin/projects');
      return response.data;
    } catch (error) {
      console.error('Error fetching projects:', error);
      throw error;
    }
  },

  createProject: async (projectData) => {
    try {
      const response = await api.post('/api/admin/projects', projectData);
      return response.data;
    } catch (error) {
      console.error('Error creating project:', error);
      throw error;
    }
  },

  updateProject: async (id, projectData) => {
    try {
      const response = await api.put(`/api/admin/projects/${id}`, projectData);
      return response.data;
    } catch (error) {
      console.error('Error updating project:', error);
      throw error;
    }
  },

  deleteProject: async (id) => {
    try {
      const response = await api.delete(`/api/admin/projects/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error deleting project:', error);
      throw error;
    }
  }
};

// Blogs API
export const blogsAPI = {
  getBlogs: async () => {
    try {
      const response = await api.get('/api/admin/blogs');
      return response.data;
    } catch (error) {
      console.error('Error fetching blogs:', error);
      throw error;
    }
  },

  createBlog: async (blogData) => {
    try {
      const response = await api.post('/api/admin/blogs', blogData);
      return response.data;
    } catch (error) {
      console.error('Error creating blog:', error);
      throw error;
    }
  },

  updateBlog: async (id, blogData) => {
    try {
      const response = await api.put(`/api/admin/blogs/${id}`, blogData);
      return response.data;
    } catch (error) {
      console.error('Error updating blog:', error);
      throw error;
    }
  },

  deleteBlog: async (id) => {
    try {
      const response = await api.delete(`/api/admin/blogs/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error deleting blog:', error);
      throw error;
    }
  }
};

// Messages API
export const messagesAPI = {
  getMessages: async () => {
    try {
      const response = await api.get('/api/admin/messages');
      return response.data;
    } catch (error) {
      console.error('Error fetching messages:', error);
      throw error;
    }
  },

  getMessage: async (id) => {
    try {
      const response = await api.get(`/api/admin/messages/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching message:', error);
      throw error;
    }
  },

  deleteMessage: async (id) => {
    try {
      const response = await api.delete(`/api/admin/messages/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error deleting message:', error);
      throw error;
    }
  },

  markAsRead: async (id) => {
    try {
      const response = await api.patch(`/api/admin/messages/${id}/read`);
      return response.data;
    } catch (error) {
      console.error('Error marking message as read:', error);
      throw error;
    }
  }
};

// Auth API
export const authAPI = {
  login: async (credentials) => {
    try {
      const response = await api.post('/api/admin/login', credentials);
      return response.data;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  },

  logout: async () => {
    try {
      const response = await api.post('/api/admin/logout');
      return response.data;
    } catch (error) {
      console.error('Logout error:', error);
      throw error;
    }
  },

  verifyToken: async () => {
    try {
      const response = await api.get('/api/admin/verify');
      return response.data;
    } catch (error) {
      console.error('Token verification error:', error);
      throw error;
    }
  }
};

// Helper function to get stats from individual endpoints
const getStatsFromIndividualEndpoints = async () => {
  try {
    const [totalProjects, totalBlogs, totalMessages] = await Promise.all([
      dashboardAPI.getTotalProjects(),
      dashboardAPI.getTotalBlogs(),
      dashboardAPI.getTotalMessages()
    ]);

    return {
      success: true,
      data: {
        totalProjects,
        totalBlogs,
        totalMessages,
        recentActivity: []
      }
    };
  } catch (error) {
    throw new Error('Failed to fetch statistics from individual endpoints');
  }
};

export default api;
