// admin-panel/src/contexts/AuthContext.js - Updated login function
const login = async (email, password) => {
  try {
    setError(null);
    setLoading(true);
    console.log('üîê Login attempt started for:', email);
    
    let response;
    
    try {
      // Try to call the real backend
      response = await authAPI.login(email, password);
      console.log('üì® Backend login response:', response);
    } catch (apiError) {
      console.error('‚ùå Backend API call failed:', apiError);
      
      // If backend is down, use mock auth
      if (apiError.message.includes('Failed to fetch') || apiError.message.includes('Network Error')) {
        console.log('üîÑ Backend seems down, using mock authentication...');
        
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 1000));
        
        // Mock successful response
        response = {
          success: true,
          data: {
            token: 'mock-jwt-token-' + Date.now(),
            user: {
              id: 1,
              email: email,
              name: 'Admin User',
              role: 'admin',
              isMock: true // Flag to indicate mock user
            }
          }
        };
      } else {
        throw apiError;
      }
    }
    
    // Handle response (same as before)
    let token, userData;
    
    if (response.data && response.data.token) {
      token = response.data.token;
      userData = response.data.user;
    } else if (response.token) {
      token = response.token;
      userData = response.user;
    } else if (response.id || response.email) {
      userData = response;
      token = response.token || 'mock-token';
    } else if (response.success && response.data) {
      token = response.data.token || response.data.accessToken;
      userData = response.data.user || response.data;
    } else {
      throw new Error('Unexpected response format from server');
    }
    
    if (!token) {
      token = 'mock-jwt-token-' + Date.now();
    }
    
    if (!userData) {
      userData = {
        id: 1,
        email: email,
        name: 'Admin User',
        role: 'admin',
        isMock: true
      };
    }
    
    // Store auth data
    localStorage.setItem('adminToken', token);
    localStorage.setItem('adminUser', JSON.stringify(userData));
    
    // Update state
    setUser(userData);
    
    console.log('‚úÖ Login successful, user state updated');
    return { success: true, user: userData };
    
  } catch (error) {
    console.error('‚ùå Login error:', error);
    const errorMessage = error.response?.data?.message || 
                        error.response?.data?.error || 
                        error.message || 
                        'Login failed. Check if backend is running.';
    setError(errorMessage);
    return { success: false, error: errorMessage };
  } finally {
    setLoading(false);
  }
};
