// admin-panel/src/services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
console.log('üîß API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
  timeout: 10000, // 10 second timeout
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  console.log(`üöÄ ${config.method?.toUpperCase()} Request to: ${config.url}`);
  return config;
});

// Handle responses globally
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ ${response.status} Response from: ${response.config.url}`, response.data);
    return response;
  },
  (error) => {
    console.error(`‚ùå API Error:`, {
      url: error.config?.url,
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    
    if (error.response?.status === 401) {
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Helper functions for localStorage
const getBlogsFromStorage = () => {
  try {
    const stored = localStorage.getItem('shared-blogs');
    return stored ? JSON.parse(stored) : [];
  } catch (error) {
    console.error('Error reading blogs from localStorage:', error);
    return [];
  }
};

const saveBlogsToStorage = (blogs) => {
  try {
    localStorage.setItem('shared-blogs', JSON.stringify(blogs));
    console.log('üíæ Saved blogs to localStorage:', blogs.length);
  } catch (error) {
    console.error('Error saving blogs to localStorage:', error);
  }
};

// Initialize sample blogs if none exist
const initializeSampleBlogs = () => {
  const existingBlogs = getBlogsFromStorage();
  if (existingBlogs.length === 0) {
    const sampleBlogs = [
      {
        _id: '1',
