    console.log('üì° Updating blog:', id, data);
    
    await new Promise(resolve => setTimeout(resolve, 600));
    
    const blogs = getBlogsFromStorage();
    const blogIndex = blogs.findIndex(blog => blog._id === id);
    
    if (blogIndex === -1) {
      throw new Error('Blog not found');
    }
    
    // Update blog with new data
    blogs[blogIndex] = {
      ...blogs[blogIndex],
      ...data,
      excerpt: data.content ? data.content.substring(0, 150) + '...' : blogs[blogIndex].excerpt,
      image: data.imageUrl || blogs[blogIndex].image,
      imageUrl: data.imageUrl || blogs[blogIndex].imageUrl,
      updatedAt: new Date().toISOString()
    };
    
    // Save back to localStorage
    saveBlogsToStorage(blogs);
    
    console.log('‚úÖ Updated blog:', blogs[blogIndex]);
    
