    }, 100);
    
    return blogs[blogIndex];
  },
  
  delete: async (id) => {
    console.log('üì° Deleting blog:', id);
    
    await new Promise(resolve => setTimeout(resolve, 400));
    
    const blogs = getBlogsFromStorage();
    const filteredBlogs = blogs.filter(blog => blog._id !== id);
    
    if (blogs.length === filteredBlogs.length) {
      throw new Error('Blog not found');
    }
    
    // Save back to localStorage
    saveBlogsToStorage(filteredBlogs);
    
    console.log('üóëÔ∏è Deleted blog:', id);
    
