import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { checkBackendConnection, authAPI } from '../../services/api';

const DebugLogin = () => {
  const [credentials, setCredentials] = useState({
    email: 'admin@example.com',
    password: 'password'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [backendStatus, setBackendStatus] = useState('checking');
  const [debugInfo, setDebugInfo] = useState('');
  const { login } = useAuth();

  // Test backend connection on component mount
  useEffect(() => {
    const testBackend = async () => {
      try {
        setDebugInfo('üîç Testing backend connection...\n');
        const response = await checkBackendConnection();
        setBackendStatus('connected');
        setDebugInfo(prev => prev + `‚úÖ Backend Connected: ${response.data.message}\n`);
        setDebugInfo(prev => prev + `üìç Backend URL: ${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}\n`);
      } catch (error) {
        setBackendStatus('disconnected');
        setDebugInfo(prev => prev + `‚ùå Backend Connection Failed: ${error.message}\n`);
        setDebugInfo(prev => prev + `üí° Make sure backend is running on port 5000\n`);
      }
    };
    
    testBackend();
  }, []);

  // Test credentials for quick testing
  const testCredentials = [
    { email: 'admin@example.com', password: 'password', description: 'Default Admin' },
    { email: 'test@test.com', password: 'test', description: 'Test User' },
    { email: 'demo@demo.com', password: 'demo', description: 'Demo User' }
  ];

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const fillTestCredentials = (test) => {
    setCredentials(test);
    setDebugInfo(prev => prev + `üîë Filled credentials: ${test.email}\n`);
  };

  // Test direct API call without AuthContext
  const testDirectLogin = async () => {
    setDebugInfo(prev => prev + `\nüöÄ Testing direct API login...\n`);
    
    try {
      const response = await authAPI.login(credentials);
      setDebugInfo(prev => prev + `‚úÖ Direct login success!\n`);
      setDebugInfo(prev => prev + `üì¶ Response: ${JSON.stringify(response.data, null, 2)}\n`);
      return response.data;
    } catch (error) {
      const errorMsg = error.response?.data?.message || error.message;
      const errorDetails = {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message
      };
      setDebugInfo(prev => prev + `‚ùå Direct login failed:\n${JSON.stringify(errorDetails, null, 2)}\n`);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setDebugInfo(prev => prev + `\n--- Login Attempt ---\n`);
    
    try {
      // Test 1: Direct API call
      setDebugInfo(prev => prev + `1. Testing direct API call...\n`);
      const directResult = await testDirectLogin();
      
      // Test 2: AuthContext login
      setDebugInfo(prev => prev + `2. Testing AuthContext login...\n`);
      const result = await login(credentials);
      
      if (result.success) {
        setDebugInfo(prev => prev + `‚úÖ AuthContext login successful! Redirecting...\n`);
        // Redirect should happen automatically via ProtectedRoute
      } else {
        setError(result.message);
        setDebugInfo(prev => prev + `‚ùå AuthContext error: ${result.message}\n`);
      }
    } catch (error) {
      const errorMsg = error.response?.data?.message || error.message;
      setError(errorMsg);
      setDebugInfo(prev => prev + `‚ùå Final error: ${errorMsg}\n`);
      
      // Check for specific error types
      if (error.message.includes('Network Error') || error.code === 'NETWORK_ERROR') {
        setDebugInfo(prev => prev + `üåê Network error - check if backend is running\n`);
      }
      if (error.response?.status === 404) {
        setDebugInfo(prev => prev + `üîç Endpoint not found - check backend routes\n`);
      }
      if (error.response?.status === 500) {
        setDebugInfo(prev => prev + `‚ö° Server error - check backend console\n`);
      }
    } finally {
      setLoading(false);
    }
  };

  const testBackendAgain = async () => {
    setDebugInfo(prev => prev + `\nüîÑ Re-testing backend connection...\n`);
    setBackendStatus('checking');
    
    try {
      const response = await checkBackendConnection();
      setBackendStatus('connected');
      setDebugInfo(prev => prev + `‚úÖ Backend reconnected: ${response.data.message}\n`);
    } catch (error) {
      setBackendStatus('disconnected');
      setDebugInfo(prev => prev + `‚ùå Backend still disconnected: ${error.message}\n`);
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <h2>üîß Debug Login</h2>
        
        {/* Backend Status */}
        <div className={`backend-status ${backendStatus}`}>
          <strong>Backend Status:</strong> {backendStatus.toUpperCase()}
          <button 
            type="button" 
            onClick={testBackendAgain}
            style={{ marginLeft: '10px', padding: '2px 8px', fontSize: '12px' }}
          >
            Retest
          </button>
        </div>
        
        {/* Test Credentials */}
        <div className="test-credentials">
          <h4>Quick Test Credentials:</h4>
          {testCredentials.map((test, index) => (
            <button 
              key={index}
              type="button"
              onClick={() => fillTestCredentials(test)}
              className="test-btn"
            >
              {test.description}
            </button>
          ))}
        </div>
        
        {/* Error Display */}
        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}
        
        {/* Form Fields */}
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={credentials.email}
            onChange={handleChange}
            required
            disabled={loading}
            placeholder="admin@example.com"
          />
        </div>
        
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
            disabled={loading}
            placeholder="password"
          />
        </div>
        
        <div className="form-actions">
          <button type="submit" disabled={loading || backendStatus !== 'connected'}>
            {loading ? 'üîÑ Logging in...' : 'üöÄ Login'}
          </button>
          
          <button 
            type="button" 
            onClick={testDirectLogin}
            disabled={loading}
            style={{ backgroundColor: '#6c757d' }}
          >
            Test API Only
          </button>
        </div>

        {/* Debug Info */}
        <div className="debug-info">
          <h4>Debug Console:</h4>
          <textarea 
            value={debugInfo}
            readOnly
            rows="8"
            style={{ 
              width: '100%', 
              fontFamily: 'monospace', 
              fontSize: '12px',
              backgroundColor: '#f8f9fa',
              border: '1px solid #dee2e6',
              borderRadius: '4px',
              padding: '8px'
            }}
          />
        </div>
      </form>
    </div>
  );
};

export default DebugLogin;
