// In your src/services/api.js, add adminAPI:
export const adminAPI = {
  // Projects management
  getProjects: () => axios.get('/api/admin/projects'),
  createProject: (projectData) => axios.post('/api/admin/projects', projectData),
  updateProject: (id, projectData) => axios.put(`/api/admin/projects/${id}`, projectData),
  deleteProject: (id) => axios.delete(`/api/admin/projects/${id}`),
  
  // Blog management
  getBlogs: () => axios.get('/api/admin/blogs'),
  createBlog: (blogData) => axios.post('/api/admin/blogs', blogData),
  updateBlog: (id, blogData) => axios.put(`/api/admin/blogs/${id}`, blogData),
  deleteBlog: (id) => axios.delete(`/api/admin/blogs/${id}`),
  
  // Contact messages
  getMessages: () => axios.get('/api/admin/messages'),
  deleteMessage: (id) => axios.delete(`/api/admin/messages/${id}`),
  
  // Authentication
  login: (credentials) => axios.post('/api/admin/login', credentials),
  verify: () => axios.get('/api/admin/verify')
};
