import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './BlogManager.css';

function BlogManager() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    excerpt: '',
    published: false,
    image: '',
    tags: ''
  });

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    setLoading(true);
    try {
      const data = await api.getBlogs();
      setBlogs(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching blogs:', error);
      alert('Failed to fetch blogs');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const blogData = {
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      if (editingBlog) {
        await api.updateBlog(editingBlog._id, blogData);
      } else {
        await api.createBlog(blogData);
      }
      setShowForm(false);
      setEditingBlog(null);
      setFormData({ 
        title: '', 
        content: '', 
        excerpt: '', 
        published: false, 
        image: '',
        tags: ''
      });
      fetchBlogs();
    } catch (error) {
      console.error('Error saving blog:', error);
      alert('Failed to save blog');
    }
  };

  const handleEdit = (blog) => {
    setEditingBlog(blog);
    setFormData({
      title: blog.title,
      content: blog.content,
      excerpt: blog.excerpt,
      published: blog.published || false,
      image: blog.image || '',
      tags: blog.tags ? blog.tags.join(', ') : ''
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await api.deleteBlog(id);
        fetchBlogs();
      } catch (error) {
        console.error('Error deleting blog:', error);
        alert('Failed to delete blog');
      }
    }
  };

  const resetForm = () => {
    setShowForm(false);
    setEditingBlog(null);
    setFormData({ 
      title: '', 
      content: '', 
      excerpt: '', 
      published: false, 
      image: '',
      tags: ''
    });
  };

  return (
    <div className="blog-manager">
      <div className="blog-header">
        <h2>Blog Management</h2>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(true)}
        >
          Add New Blog
        </button>
      </div>

      {showForm && (
        <div className="blog-form-modal">
          <div className="blog-form">
            <h3>{editingBlog ? 'Edit Blog' : 'Add New Blog'}</h3>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Title"
                value={formData.title}
                onChange={(e) => setFormData({...formData, title: e.target.value})}
                required
              />
              <textarea
                placeholder="Excerpt"
                value={formData.excerpt}
                onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
                rows="3"
              />
              <textarea
                placeholder="Content"
                value={formData.content}
                onChange={(e) => setFormData({...formData, content: e.target.value})}
                rows="10"
                required
              />
              <input
                type="text"
                placeholder="Image URL (optional)"
                value={formData.image}
                onChange={(e) => setFormData({...formData, image: e.target.value})}
              />
              <input
                type="text"
                placeholder="Tags (comma separated)"
                value={formData.tags}
                onChange={(e) => setFormData({...formData, tags: e.target.value})}
              />
              <label>
                <input
                  type="checkbox"
                  checked={formData.published}
                  onChange={(e) => setFormData({...formData, published: e.target.checked})}
                />
                Published (Visible on frontend)
              </label>
              <div className="form-actions">
                <button type="submit" className="btn-primary">
                  {editingBlog ? 'Update' : 'Create'} Blog
                </button>
                <button type="button" onClick={resetForm} className="btn-secondary">
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {loading ? (
        <div className="loading">Loading blogs...</div>
      ) : (
        <div className="blogs-list">
          {blogs.map(blog => (
            <div key={blog._id} className="blog-item">
              <div className="blog-info">
                <h4>{blog.title}</h4>
                <p className="blog-excerpt">{blog.excerpt}</p>
                <div className="blog-meta">
                  <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                    {blog.published ? 'Published' : 'Draft'}
                  </span>
                  {blog.tags && blog.tags.length > 0 && (
                    <div className="blog-tags">
                      {blog.tags.map(tag => (
                        <span key={tag} className="tag">{tag}</span>
                      ))}
                    </div>
                  )}
                </div>
                {blog.image && (
                  <div className="blog-image-preview">
                    <img src={blog.image} alt={blog.title} />
                  </div>
                )}
              </div>
              <div className="blog-actions">
                <button onClick={() => handleEdit(blog)} className="btn-edit">
                  Edit
                </button>
                <button onClick={() => handleDelete(blog._id)} className="btn-delete">
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default BlogManager;
