import React, { useState } from 'react';

const BlogManager = () => {
  const [blogs, setBlogs] = useState([
    {
      id: 1,
      title: 'Getting Started with React',
      author: 'John Doe',
      category: 'Technology',
      status: 'published',
      date: '2024-01-15',
      views: 1245
    },
    {
      id: 2,
      title: 'Web Design Best Practices',
      author: 'Jane Smith',
      category: 'Design',
      status: 'published',
      date: '2024-01-10',
      views: 892
    },
    {
      id: 3,
      title: 'Upcoming Project Insights',
      author: 'Mike Johnson',
      category: 'Business',
      status: 'draft',
      date: '2024-01-18',
      views: 0
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedBlog, setSelectedBlog] = useState(null);

  const filteredBlogs = blogs.filter(blog =>
    blog.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    blog.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
    blog.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleEdit = (blog) => {
    setSelectedBlog(blog);
    setShowModal(true);
  };

  const handleDelete = (blogId) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      setBlogs(blogs.filter(blog => blog.id !== blogId));
    }
  };

  const getStatusBadge = (status) => {
    const statusClass = {
      published: 'status-published',
      draft: 'status-draft',
      archived: 'status-archived'
    }[status] || 'status-draft';

    return <span className={`status-badge ${statusClass}`}>{status}</span>;
  };

  return (
    <div>
      <div className="management-header">
        <h2 className="management-title">Blog Management</h2>
        <div className="management-actions">
          <div className="search-box">
            <input
              type="text"
              className="search-input"
              placeholder="Search blogs..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <span className="search-icon">üîç</span>
          </div>
          <button className="btn btn-primary btn-add">
            <span>+</span> Add New Blog
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-body">
          {filteredBlogs.length > 0 ? (
            <table className="data-table">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Author</th>
                  <th>Category</th>
                  <th>Status</th>
                  <th>Date</th>
                  <th>Views</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredBlogs.map(blog => (
                  <tr key={blog.id}>
                    <td>
                      <strong>{blog.title}</strong>
                    </td>
                    <td>{blog.author}</td>
                    <td>
                      <span className="badge badge-secondary">{blog.category}</span>
                    </td>
                    <td>{getStatusBadge(blog.status)}</td>
                    <td>{new Date(blog.date).toLocaleDateString()}</td>
                    <td>{blog.views.toLocaleString()}</td>
                    <td>
                      <div className="table-actions">
                        <button 
                          className="action-btn btn-view"
                          onClick={() => handleEdit(blog)}
                        >
                          üëÅÔ∏è Edit
                        </button>
                        <button 
                          className="action-btn btn-delete"
                          onClick={() => handleDelete(blog.id)}
                        >
                          üóëÔ∏è Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <div className="empty-state">
              <div className="empty-state-icon">üìù</div>
              <div className="empty-state-text">No blogs found</div>
              <div className="empty-state-subtext">
                {searchTerm ? 'Try adjusting your search terms' : 'Get started by creating your first blog post'}
              </div>
              <button className="btn btn-primary">
                + Create New Blog
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Blog Modal */}
      {showModal && selectedBlog && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3 className="modal-title">Edit Blog</h3>
              <button 
                className="modal-close"
                onClick={() => setShowModal(false)}
              >
                √ó
              </button>
            </div>
            <div className="modal-body">
              <p>Edit form for: <strong>{selectedBlog.title}</strong></p>
              {/* Add your form fields here */}
            </div>
            <div className="modal-footer">
              <button 
                className="btn"
                onClick={() => setShowModal(false)}
              >
                Cancel
              </button>
              <button className="btn btn-primary">
                Save Changes
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BlogManager;
