        formData.append('featuredImage', blogData.featuredImage);
      }

      const response = await fetch(`${API_BASE_URL}/api/blog/${id}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('adminToken')}`,
