import React, { useState, useEffect, useCallback } from 'react';
import { blogAPI, mockBlogAPI } from '../services/api';

const BlogsPage = () => {
  const [blogs, setBlogs] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    excerpt: '',
    content: '',
    image: '',
    icon: 'fas fa-pencil-alt',
    tags: ''
  });
  const [loading, setLoading] = useState(false);

  // Use mock API for development, real API for production
  const API = process.env.NODE_ENV === 'development' ? mockBlogAPI : blogAPI;

  // Use useCallback to memoize the fetchBlogs function
  const fetchBlogs = useCallback(async () => {
    try {
      setLoading(true);
      const response = await API.getAllBlogs();
      setBlogs(response.data);
    } catch (error) {
      console.error('Error fetching blogs:', error);
      // Fallback to mock data
      const response = await mockBlogAPI.getAllBlogs();
      setBlogs(response.data);
    } finally {
      setLoading(false);
    }
  }, [API]); // Add API as dependency

  useEffect(() => {
    fetchBlogs();
  }, [fetchBlogs]); // Now fetchBlogs is stable and can be included in dependencies

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const blogData = {
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      await API.createBlog(blogData);
      
      // Reset form and refresh blogs
      setFormData({
        title: '',
        excerpt: '',
        content: '',
        image: '',
        icon: 'fas fa-pencil-alt',
        tags: ''
      });
      setShowForm(false);
      fetchBlogs(); // Call the memoized function
      
      alert('Blog created successfully!');
    } catch (error) {
      console.error('Error creating blog:', error);
      alert('Error creating blog. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const deleteBlog = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        // In a real app, you would call API.deleteBlog(id)
        setBlogs(prev => prev.filter(blog => blog.id !== id));
        alert('Blog deleted successfully!');
      } catch (error) {
        console.error('Error deleting blog:', error);
        alert('Error deleting blog. Please try again.');
      }
    }
  };

  return (
    <div className="page">
      <div className="page-header">
        <h1>Blog Management</h1>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(true)}
        >
          <i className="fas fa-plus"></i>
          Add New Blog
        </button>
      </div>

      {/* Add Blog Form Overlay */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Add New Blog</h3>
              <button 
                className="close-btn"
                onClick={() => setShowForm(false)}
              >
                <i className="fas fa-times"></i>
              </button>
            </div>

            <form onSubmit={handleSubmit} className="blog-form">
              <div className="form-group">
                <label>Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter blog title"
                />
              </div>

              <div className="form-group">
                <label>Excerpt *</label>
                <textarea
                  name="excerpt"
                  value={formData.excerpt}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter brief description"
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label>Content *</label>
                <textarea
                  name="content"
                  value={formData.content}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter full blog content"
                  rows="6"
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Icon</label>
                  <select
                    name="icon"
                    value={formData.icon}
                    onChange={handleInputChange}
                  >
                    <option value="fas fa-pencil-alt">üìù General</option>
                    <option value="fas fa-code">üíª Code</option>
                    <option value="fas fa-palette">üé® Design</option>
                    <option value="fas fa-mobile-alt">üì± Mobile</option>
                    <option value="fas fa-database">üóÑÔ∏è Database</option>
                    <option value="fas fa-rocket">üöÄ DevOps</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Tags</label>
                  <input
                    type="text"
                    name="tags"
                    value={formData.tags}
                    onChange={handleInputChange}
                    placeholder="react, javascript, web"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Image URL (Optional)</label>
                <input
                  type="url"
                  name="image"
                  value={formData.image}
                  onChange={handleInputChange}
                  placeholder="https://example.com/image.jpg"
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="btn-secondary"
                  onClick={() => setShowForm(false)}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Publishing...' : 'Publish Blog'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Blogs List */}
      <div className="page-content">
        {loading && blogs.length === 0 ? (
          <div className="loading">Loading blogs...</div>
        ) : (
          <>
            <div className="stats-card">
              <h3>Blog Statistics</h3>
              <p>Total Blogs: <strong>{blogs.length}</strong></p>
            </div>

            <div className="blogs-grid">
              {blogs.map(blog => (
                <div key={blog.id} className="blog-card">
                  <div className="blog-card-header">
                    <div className="blog-icon">
                      <i className={blog.icon}></i>
                    </div>
                    <div className="blog-actions">
                      <button className="btn-icon edit-btn">
                        <i className="fas fa-edit"></i>
                      </button>
                      <button 
                        className="btn-icon delete-btn"
                        onClick={() => deleteBlog(blog.id)}
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    </div>
                  </div>

                  <div className="blog-card-content">
                    <h3>{blog.title}</h3>
                    <p className="blog-excerpt">{blog.excerpt}</p>
                    <div className="blog-meta">
                      <span className="blog-date">
                        <i className="far fa-calendar"></i>
                        {new Date(blog.date).toLocaleDateString()}
                      </span>
                      <span className="blog-read-time">{blog.readTime}</span>
                    </div>
                    <div className="blog-tags">
                      {blog.tags && blog.tags.map((tag, index) => (
                        <span key={index} className="tag">{tag}</span>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {blogs.length === 0 && (
              <div className="empty-state">
                <i className="fas fa-blog"></i>
                <h3>No Blogs Yet</h3>
                <p>Start by creating your first blog post!</p>
                <button 
                  className="btn-primary"
                  onClick={() => setShowForm(true)}
                >
                  Create First Blog
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default BlogsPage;
