import React, { useState, useEffect } from 'react';
import BlogManager from '../components/BlogManager';
import { blogsAPI } from '../services/api';

function BlogsPage() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    try {
      setLoading(true);
      const response = await blogsAPI.getAll();
      
      // üêõ DEBUG: See what the API actually returns
      console.log('üîç DEBUG - Full API response:', response);
      console.log('üîç DEBUG - Response type:', typeof response);
      console.log('üîç DEBUG - Response data:', response?.data);
      console.log('üîç DEBUG - Is array:', Array.isArray(response));
      console.log('üîç DEBUG - Is array data:', Array.isArray(response?.data));
      
      // Safe data extraction with better debugging
      let blogsData = [];
      
      if (Array.isArray(response)) {
        console.log('‚úÖ Using response as array');
        blogsData = response;
      } else if (response && Array.isArray(response.data)) {
        console.log('‚úÖ Using response.data as array');
        blogsData = response.data;
      } else if (response && response.data) {
        console.log('‚úÖ Wrapping response.data in array');
        blogsData = [response.data];
      } else {
        console.log('‚ùå No valid data found, using empty array');
        blogsData = [];
      }
      
      console.log('‚úÖ Final blogs data:', blogsData);
      console.log('‚úÖ Final blogs data length:', blogsData.length);
      setBlogs(blogsData);
    } catch (error) {
      console.error('‚ùå Error fetching blogs:', error);
      console.error('‚ùå Error details:', error.message);
      setBlogs([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBlog = async (blogData) => {
    try {
      await blogsAPI.create(blogData);
      await fetchBlogs();
      setShowForm(false);
    } catch (error) {
      console.error('Error creating blog:', error);
    }
  };

  const handleUpdateBlog = async (blogData) => {
    try {
      await blogsAPI.update(blogData.id, blogData);
      await fetchBlogs();
      setEditingBlog(null);
    } catch (error) {
      console.error('Error updating blog:', error);
    }
  };

  const handleDeleteBlog = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await blogsAPI.delete(id);
        await fetchBlogs();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  if (loading) {
    return (
      <div className="blogs-page">
        <div className="loading">Loading blogs...</div>
      </div>
    );
  }

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Manage Blog Posts</h1>
        <button 
          onClick={() => setShowForm(true)} 
          className="btn-primary"
        >
          Create New Blog
        </button>
        
        {/* üêõ TEMPORARY: Debug button */}
        <button 
          onClick={fetchBlogs} 
          className="btn-secondary"
          style={{marginLeft: '10px'}}
        >
          Test API
        </button>
      </div>

      {/* ‚úÖ FIX: Add safe check for blogs array */}
      {blogs && blogs.length === 0 ? (
        <div className="no-blogs">
          <p>No blog posts found.</p>
          <p>Create your first blog post to get started!</p>
        </div>
      ) : (
        <div className="blogs-list">
          {/* ‚úÖ FIX: Add safe mapping with proper keys */}
          {blogs.map(blog => (
            <div key={blog._id || blog.id} className="blog-item">
              <div className="blog-info">
                <h3>{blog.title}</h3>
                <p className="blog-excerpt">{blog.excerpt}</p>
                <div className="blog-meta">
                  <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                    {blog.published ? 'Published' : 'Draft'}
                  </span>
                  <span className="date">
                    {new Date(blog.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>
              
              <div className="blog-actions">
                <button 
                  onClick={() => setEditingBlog(blog)}
                  className="btn-edit"
                >
                  Edit
                </button>
                <button 
                  onClick={() => handleDeleteBlog(blog._id || blog.id)}
                  className="btn-delete"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {showForm && (
        <BlogManager
          onSave={handleCreateBlog}
          onCancel={() => setShowForm(false)}
        />
      )}

      {editingBlog && (
        <BlogManager
          blog={editingBlog}
          onSave={handleUpdateBlog}
          onCancel={() => setEditingBlog(null)}
        />
      )}
    </div>
  );
}

export default BlogsPage;
