// services/api.js - Minimal working version

// Simple API call function
async function apiCall(endpoint, options = {}) {
  // For now, just use mock data - remove this when you have a real backend
  throw new Error('No backend connected - using mock data');
}

// Mock data functions only (no real API calls)
export const authAPI = {
  demoLogin: async (credentials) => {
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    if (credentials.username === 'admin' && credentials.password === 'admin123') {
      return {
        success: true,
        token: 'demo-token',
        user: { id: 1, username: 'admin', role: 'administrator' }
      };
    } else {
      throw new Error('Invalid credentials. Use: admin / admin123');
    }
  }
};

export const blogsAPI = {
  getAll: async () => {
    await new Promise(resolve => setTimeout(resolve, 1000));
    return [
      {
        id: 1,
        title: 'Getting Started with React',
        content: 'Learn React basics.',
        author: 'Admin',
        published: true,
        createdAt: '2024-01-15',
        category: 'React'
      }
    ];
  },
  create: async (data) => {
    await new Promise(resolve => setTimeout(resolve, 800));
    return { id: Date.now(), ...data, createdAt: new Date().toISOString() };
  },
  update: async (id, data) => {
    await new Promise(resolve => setTimeout(resolve, 600));
    return { id, ...data, updatedAt: new Date().toISOString() };
  },
  delete: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 400));
    return { success: true };
  }
};

export const projectsAPI = {
  getAll: async () => {
    await new Promise(resolve => setTimeout(resolve, 1000));
    return [
      {
        id: 1,
        name: 'Sample Project',
        description: 'Project description',
        status: 'active',
        technologies: ['React', 'Node.js'],
        createdAt: '2024-01-12'
      }
    ];
  },
  create: async (data) => {
    await new Promise(resolve => setTimeout(resolve, 800));
    return { id: Date.now(), ...data, createdAt: new Date().toISOString() };
  },
  update: async (id, data) => {
    await new Promise(resolve => setTimeout(resolve, 600));
    return { id, ...data, updatedAt: new Date().toISOString() };
  },
  delete: async (id) => {
    await new Promise(resolve => setTimeout(resolve, 400));
    return { success: true };
  }
};

export const dashboardAPI = {
  getStats: async () => {
    await new Promise(resolve => setTimeout(resolve, 800));
    return {
      totalBlogs: 3,
      totalProjects: 2,
      publishedBlogs: 2,
      activeProjects: 1,
    };
  }
};

// Mock APIs for components that need them
export const mockBlogAPI = blogsAPI;
export const mockProjectAPI = projectsAPI;

export default { authAPI, blogsAPI, projectsAPI, dashboardAPI, mockBlogAPI, mockProjectAPI };
