import axios from 'axios';

// Create axios instance with base config
const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE_URL, // e.g., https://zmo-backend.onrender.com/api
  timeout: Number(process.env.REACT_APP_API_TIMEOUT) || 15000,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: false, // Set true only if backend supports cookies
});

// Request interceptor to attach token
API.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor for logging/debug
API.interceptors.response.use(
  (response) => response.data,
  (error) => {
    console.error('API Error:', error.response || error.message);
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: (email, password) => API.post('/auth/login', { email, password }),
  verify: (token) =>
    API.get('/auth/verify', {
      headers: { Authorization: `Bearer ${token}` },
    }),
  logout: () => API.post('/auth/logout'),
};

// Admin API
export const adminAPI = {
  dashboardStats: () => API.get('/admin/dashboard/stats'),
  getBlogs: () => API.get('/admin/blogs'),
};
