import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './BlogManager.css';

function BlogManager() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    excerpt: '',
    published: false
  });

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    setLoading(true);
    try {
      const data = await api.getBlogs();
      setBlogs(data);
    } catch (error) {
      console.error('Error fetching blogs:', error);
      alert('Failed to fetch blogs');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingBlog) {
        await api.updateBlog(editingBlog.id, formData);
      } else {
        await api.createBlog(formData);
      }
      setShowForm(false);
      setEditingBlog(null);
      setFormData({ title: '', content: '', excerpt: '', published: false });
      fetchBlogs();
    } catch (error) {
      console.error('Error saving blog:', error);
      alert('Failed to save blog');
    }
  };

  const handleEdit = (blog) => {
    setEditingBlog(blog);
    setFormData({
      title: blog.title,
      content: blog.content,
      excerpt: blog.excerpt,
      published: blog.published
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await api.deleteBlog(id);
        fetchBlogs();
      } catch (error) {
        console.error('Error deleting blog:', error);
        alert('Failed to delete blog');
      }
    }
  };

  const resetForm = () => {
    setShowForm(false);
    setEditingBlog(null);
    setFormData({ title: '', content: '', excerpt: '', published: false });
  };

  return (
    <div className="blog-manager">
      <div className="blog-header">
        <h2>Blog Management</h2>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(true)}
        >
          Add New Blog
        </button>
      </div>

      {showForm && (
        <div className="blog-form-modal">
          <div className="blog-form">
            <h3>{editingBlog ? 'Edit Blog' : 'Add New Blog'}</h3>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Title"
                value={formData.title}
                onChange={(e) => setFormData({...formData, title: e.target.value})}
                required
              />
              <textarea
                placeholder="Excerpt"
                value={formData.excerpt}
                onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
                rows="3"
              />
              <textarea
                placeholder="Content"
                value={formData.content}
                onChange={(e) => setFormData({...formData, content: e.target.value})}
                rows="10"
                required
              />
              <label>
                <input
                  type="checkbox"
                  checked={formData.published}
                  onChange={(e) => setFormData({...formData, published: e.target.checked})}
                />
                Published
              </label>
              <div className="form-actions">
                <button type="submit" className="btn-primary">
                  {editingBlog ? 'Update' : 'Create'} Blog
                </button>
                <button type="button" onClick={resetForm} className="btn-secondary">
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {loading ? (
        <div className="loading">Loading blogs...</div>
      ) : (
        <div className="blogs-list">
          {blogs.map(blog => (
            <div key={blog.id} className="blog-item">
              <div className="blog-info">
                <h4>{blog.title}</h4>
                <p>{blog.excerpt}</p>
                <span className={`status ${blog.published ? 'published' : 'draft'}`}>
                  {blog.published ? 'Published' : 'Draft'}
                </span>
              </div>
              <div className="blog-actions">
                <button onClick={() => handleEdit(blog)} className="btn-edit">
                  Edit
                </button>
                <button onClick={() => handleDelete(blog.id)} className="btn-delete">
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default BlogManager;
