import React, { useState, useEffect } from 'react';
import { dashboardAPI } from '../services/api';
import './DashboardPage.css';

const DashboardPage = ({ user }) => {
  const [stats, setStats] = useState({});
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [statsData, activityData] = await Promise.all([
        dashboardAPI.getStats(),
        dashboardAPI.getRecentActivity()
      ]);
      setStats(statsData);
      setRecentActivity(activityData);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading">Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-page">
      <div className="dashboard-header">
        <h1>Dashboard</h1>
        <p>Welcome back, {user?.username || 'Admin'}! üëã</p>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìù</div>
          <div className="stat-info">
            <h3>{stats.totalBlogs || 0}</h3>
            <p>Total Blogs</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üöÄ</div>
          <div className="stat-info">
            <h3>{stats.totalProjects || 0}</h3>
            <p>Total Projects</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-info">
            <h3>{stats.publishedBlogs || 0}</h3>
            <p>Published Blogs</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚ö°</div>
          <div className="stat-info">
            <h3>{stats.activeProjects || 0}</h3>
            <p>Active Projects</p>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="recent-activity">
        <h2>Recent Activity</h2>
        <div className="activity-list">
          {recentActivity.length === 0 ? (
            <div className="no-activity">No recent activity</div>
          ) : (
            recentActivity.map(activity => (
              <div key={activity.id} className="activity-item">
                <div className="activity-icon">
                  {activity.action === 'blog_created' && 'üìù'}
                  {activity.action === 'project_updated' && 'üöÄ'}
                  {activity.action === 'user_login' && 'üë§'}
                </div>
                <div className="activity-content">
                  <p className="activity-title">{activity.title}</p>
                  <span className="activity-time">
                    {new Date(activity.timestamp).toLocaleString()}
                  </span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="action-buttons">
          <a href="/blogs" className="action-btn">
            <span className="action-icon">‚úèÔ∏è</span>
            Write New Blog
          </a>
          <a href="/projects" className="action-btn">
            <span className="action-icon">üÜï</span>
            Add New Project
          </a>
          <button className="action-btn">
            <span className="action-icon">üìä</span>
            View Analytics
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
