import React, { useState } from 'react';
import './ProjectManagement.css';

const ProjectManagement = ({ projects = [], onEdit, onDelete, onCreateNew }) => {
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  // Filter projects based on featured status and search term
  const filteredProjects = projects.filter(project => {
    if (!project) return false;
    
    const matchesFilter = filter === 'all' || 
                         (filter === 'featured' && project.featured) ||
                         (filter === 'regular' && !project.featured);
    
    const matchesSearch = project.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.technologies?.some(tech => 
                           tech?.toLowerCase().includes(searchTerm.toLowerCase())
                         );
    
    return matchesFilter && matchesSearch;
  });

  return (
    <div className="project-management">
      <div className="management-header">
        <h1>Project Management</h1>
        <button className="btn-primary" onClick={onCreateNew}>
          <i className="fas fa-plus"></i>
          Create New Project
        </button>
      </div>

      {/* Filters and Search */}
      <div className="management-controls">
        <div className="search-box">
          <i className="fas fa-search"></i>
          <input
            type="text"
            placeholder="Search projects by name, description, or technologies..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="filter-tabs">
          <button 
            className={`tab ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            All Projects
          </button>
          <button 
            className={`tab ${filter === 'featured' ? 'active' : ''}`}
            onClick={() => setFilter('featured')}
          >
            Featured
          </button>
          <button 
            className={`tab ${filter === 'regular' ? 'active' : ''}`}
            onClick={() => setFilter('regular')}
          >
            Regular
          </button>
        </div>
      </div>

      {/* Projects List - No Photos */}
      <div className="projects-list">
        {filteredProjects.length === 0 ? (
          <div className="empty-state">
            <i className="fas fa-folder-open"></i>
            <h3>
              {projects.length === 0 
                ? 'No projects found' 
                : 'No projects match your search'
              }
            </h3>
            <p>
              {searchTerm || filter !== 'all' 
                ? 'Try adjusting your search or filter criteria'
                : 'Get started by creating your first project'
              }
            </p>
            {!searchTerm && filter === 'all' && projects.length === 0 && (
              <button className="btn-primary" onClick={onCreateNew}>
                Create Your First Project
              </button>
            )}
          </div>
        ) : (
          <div className="projects-grid">
            {filteredProjects.map(project => (
              <div key={project._id} className="project-card">
                <div className="project-header">
                  <div className="project-title-section">
                    <h3 className="project-name">{project.name || 'Untitled Project'}</h3>
                    {project.featured && (
                      <span className="featured-badge">
                        <i className="fas fa-star"></i>
                        Featured
                      </span>
                    )}
                  </div>
                  <div className="project-actions">
                    <button 
                      className="btn-edit"
                      onClick={() => onEdit(project)}
                      title="Edit Project"
                    >
                      <i className="fas fa-edit"></i>
                      Edit
                    </button>
                    <button 
                      className="btn-delete"
                      onClick={() => onDelete(project._id)}
                      title="Delete Project"
                    >
                      <i className="fas fa-trash"></i>
                      Delete
                    </button>
                  </div>
                </div>

                <div className="project-content">
                  <p className="project-description">
                    {project.description && project.description.length > 150 
                      ? `${project.description.substring(0, 150)}...`
                      : project.description || 'No description available'
                    }
                  </p>

                  <div className="project-technologies">
                    {project.technologies?.slice(0, 4).map((tech, index) => (
                      <span key={index} className="tech-tag">
                        {tech}
                      </span>
                    ))}
                    {project.technologies && project.technologies.length > 4 && (
                      <span className="tech-tag-more">
                        +{project.technologies.length - 4} more
                      </span>
                    )}
                    {(!project.technologies || project.technologies.length === 0) && (
                      <span className="tech-tag-more">No technologies listed</span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Summary Stats */}
      {filteredProjects.length > 0 && (
        <div className="management-footer">
          <div className="stats">
            <span className="stat">
              Total: <strong>{projects.length}</strong>
            </span>
            <span className="stat">
              Featured: <strong>{projects.filter(p => p?.featured).length}</strong>
            </span>
            <span className="stat">
              Showing: <strong>{filteredProjects.length}</strong>
            </span>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectManagement;
