import React, { useState, useEffect } from 'react';
import { blogAPI } from '../services/api';

const BlogManager = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    try {
      const response = await blogAPI.getAll();
      setBlogs(response.data);
    } catch (error) {
      setError('Failed to load blogs');
      console.error('Error fetching blogs:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this blog post?')) {
      return;
    }

    try {
      await blogAPI.delete(id);
      setBlogs(blogs.filter(blog => blog._id !== id));
    } catch (error) {
      setError('Failed to delete blog post');
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      await blogAPI.updateStatus(id, newStatus);
      setBlogs(blogs.map(blog => 
        blog._id === id ? { ...blog, status: newStatus } : blog
      ));
    } catch (error) {
      setError('Failed to update status');
    }
  };

  if (loading) return <div className="loading">Loading blogs...</div>;

  return (
    <div className="blog-manager">
      <div className="manager-header">
        <h2>Blog Posts Management</h2>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(true)}
        >
          + Add New Post
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Status</th>
              <th>Created</th>
              <th>Views</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {blogs.map(blog => (
              <tr key={blog._id}>
                <td>
                  <div className="blog-title-cell">
                    <strong>{blog.title}</strong>
                    {blog.slug && (
                      <small>/{blog.slug}</small>
                    )}
                  </div>
                </td>
                <td>
                  <select
                    value={blog.status}
                    onChange={(e) => handleStatusChange(blog._id, e.target.value)}
                    className={`status-select status-${blog.status}`}
                  >
                    <option value="draft">Draft</option>
                    <option value="published">Published</option>
                    <option value="archived">Archived</option>
                  </select>
                </td>
                <td>{new Date(blog.createdAt).toLocaleDateString()}</td>
                <td>{blog.viewCount || 0}</td>
                <td>
                  <div className="action-buttons">
                    <button 
                      className="btn-edit"
                      onClick={() => setEditingBlog(blog)}
                    >
                      Edit
                    </button>
                    <button 
                      className="btn-delete"
                      onClick={() => handleDelete(blog._id)}
                    >
                      Delete
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {blogs.length === 0 && (
          <div className="no-data">
            <p>No blog posts found. Create your first post!</p>
          </div>
        )}
      </div>

      {/* Blog Form Modal would go here */}
      {(showForm || editingBlog) && (
        <BlogForm 
          blog={editingBlog}
          onClose={() => {
            setShowForm(false);
            setEditingBlog(null);
          }}
          onSave={fetchBlogs}
        />
      )}
    </div>
  );
};

// Blog Form Component (simplified version)
const BlogForm = ({ blog, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    title: blog?.title || '',
    slug: blog?.slug || '',
    content: blog?.content || '',
    excerpt: blog?.excerpt || '',
    status: blog?.status || 'draft',
    tags: blog?.tags?.join(', ') || ''
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const submitData = {
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      if (blog) {
        await blogAPI.update(blog._id, submitData);
      } else {
        await blogAPI.create(submitData);
      }

      onSave();
      onClose();
    } catch (error) {
      console.error('Error saving blog:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>{blog ? 'Edit Blog Post' : 'Create New Blog Post'}</h3>
          <button onClick={onClose} className="close-btn">Ã—</button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title *</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Slug *</label>
            <input
              type="text"
              value={formData.slug}
              onChange={(e) => setFormData({...formData, slug: e.target.value})}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Content *</label>
            <textarea
              value={formData.content}
              onChange={(e) => setFormData({...formData, content: e.target.value})}
              rows="10"
              required
            />
          </div>
          
          <div className="form-actions">
            <button type="button" onClick={onClose}>Cancel</button>
            <button type="submit" disabled={loading}>
              {loading ? 'Saving...' : 'Save Post'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BlogManager;
