import React, { useState } from 'react';
import './BlogManager.css';

const BlogManager = ({ blog, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: blog?.title || '',
    content: blog?.content || '',
    excerpt: blog?.excerpt || '',
    image: blog?.image || '',
    tags: blog?.tags?.join(', ') || '',
    published: blog?.published || false
  });
  const [imagePreview, setImagePreview] = useState(blog?.image || '');
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file size (5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('File size must be less than 5MB');
        return;
      }

      // Validate file type
      if (!file.type.startsWith('image/')) {
        alert('Please select an image file');
        return;
      }

      setSelectedFile(file);
      
      // Create temporary preview
      const previewUrl = URL.createObjectURL(file);
      setImagePreview(previewUrl);
    }
  };

  const handleUrlChange = (e) => {
    const url = e.target.value;
    setFormData({...formData, image: url});
    setImagePreview(url);
    setSelectedFile(null);
  };

  const uploadImageToServer = async (file) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await fetch('http://localhost:5000/api/upload/image', {
        method: 'POST',
        body: formData,
        // Don't set Content-Type header, let browser set it with boundary
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Upload failed');
      }

      const data = await response.json();
      return data.imageUrl;
    } catch (error) {
      console.error('Upload error:', error);
      throw new Error(error.message || 'Failed to upload image');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setUploading(true);

    try {
      let finalImageUrl = formData.image;
      
      // Upload local image to server if selected
      if (selectedFile) {
        console.log('Uploading image to server...');
        finalImageUrl = await uploadImageToServer(selectedFile);
        console.log('Image uploaded successfully:', finalImageUrl);
        
        // Clean up temporary URL
        if (imagePreview.startsWith('blob:')) {
          URL.revokeObjectURL(imagePreview);
        }
      }
      
      const blogData = {
        title: formData.title,
        content: formData.content,
        excerpt: formData.excerpt,
        image: finalImageUrl, // This is now a permanent URL like "/uploads/blog-123456789.jpg"
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        published: formData.published
      };
      
      if (blog?._id) {
        blogData._id = blog._id;
      }
      
      console.log('Saving blog data:', blogData);
      onSave(blogData);
    } catch (error) {
      alert(`Error: ${error.message}`);
      console.error('Submit error:', error);
    } finally {
      setUploading(false);
    }
  };

  const clearImage = () => {
    // Clean up temporary URL if it's a blob URL
    if (imagePreview.startsWith('blob:')) {
      URL.revokeObjectURL(imagePreview);
    }
    
    setImagePreview('');
    setFormData({...formData, image: ''});
    setSelectedFile(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{blog ? 'Edit Blog' : 'Create New Blog'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Content</label>
            <textarea
              value={formData.content}
              onChange={(e) => setFormData({...formData, content: e.target.value})}
              rows="6"
              required
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Excerpt</label>
            <textarea
              value={formData.excerpt}
              onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
              rows="3"
              disabled={uploading}
            />
          </div>

          {/* Image Section */}
          <div className="form-group">
            <label>Blog Image</label>
            
            {/* Image Preview */}
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" />
                <button 
                  type="button" 
                  className="clear-image-btn" 
                  onClick={clearImage}
                  disabled={uploading}
                >
                  Remove Image
                </button>
              </div>
            )}

            {/* Local File Upload */}
            <div className="image-upload-section">
              <label className="upload-label">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="file-input"
                  disabled={uploading}
                />
                <span className={`upload-button ${uploading ? 'uploading' : ''}`}>
                  {uploading ? 'Uploading...' : 'Choose Local Image'}
                </span>
              </label>
              <span className="upload-hint">or</span>
            </div>

            {/* URL Input */}
            <input
              type="url"
              value={formData.image}
              onChange={handleUrlChange}
              placeholder="Or enter image URL"
              className="url-input"
              disabled={uploading}
            />
            
            <div className="image-notes">
              <small>Supported formats: JPG, PNG, GIF, WebP â€¢ Max size: 5MB</small>
              {selectedFile && (
                <div className="file-info">
                  <small>Selected: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)</small>
                </div>
              )}
            </div>
          </div>

          <div className="form-group">
            <label>Tags (comma separated)</label>
            <input
              type="text"
              value={formData.tags}
              onChange={(e) => setFormData({...formData, tags: e.target.value})}
              disabled={uploading}
            />
          </div>

          <div className="form-group checkbox">
            <label>
              <input
                type="checkbox"
                checked={formData.published}
                onChange={(e) => setFormData({...formData, published: e.target.checked})}
                disabled={uploading}
              />
              Published
            </label>
          </div>

          <div className="form-actions">
            <button 
              type="submit" 
              className="btn-primary" 
              disabled={uploading}
            >
              {uploading ? 'Uploading...' : (blog ? 'Update Blog' : 'Create Blog')}
            </button>
            <button 
              type="button" 
              className="btn-secondary" 
              onClick={onCancel}
              disabled={uploading}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BlogManager;
