// components/Login.js
import React, { useState } from 'react';
import './Login.css';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    username: '',
    password: ''
  });
  const [rememberMe, setRememberMe] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    if (!credentials.username || !credentials.password) {
      setError('Please enter both username and password');
      return;
    }

    setIsLoading(true);
    
    try {
      // Simulate API call - remove authAPI import since we're not using real API yet
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Demo validation
      if (credentials.username === 'admin' && credentials.password === 'admin123') {
        onLogin({
          id: 1,
          username: 'admin',
          role: 'administrator'
        });
      } else {
        setError('Invalid credentials. Use: admin / admin123');
      }
    } catch (error) {
      setError('Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
    if (error) setError('');
  };

  const fillDemoCredentials = () => {
    setCredentials({
      username: 'admin',
      password: 'admin123'
    });
    setError('');
  };

  return (
    <div className="login-container">
      {/* Your existing JSX - keep this part the same */}
      <div className="login-card">
        {/* Left Panel - Branding */}
        <div className="brand-panel">
          <div className="brand-content">
            <div className="logo">
              <div className="logo-icon">‚ö°</div>
              <span className="logo-text">ZMO</span>
            </div>
            <h1>Admin Portal</h1>
            <p className="brand-subtitle">
              Secure access to your dashboard and management tools
            </p>
            <div className="features-list">
              <div className="feature">
                <span className="feature-icon">üîí</span>
                <span>Enterprise Security</span>
              </div>
              <div className="feature">
                <span className="feature-icon">üìä</span>
                <span>Real-time Analytics</span>
              </div>
              <div className="feature">
                <span className="feature-icon">üöÄ</span>
                <span>High Performance</span>
              </div>
            </div>
          </div>
        </div>

        {/* Right Panel - Login Form */}
        <div className="form-panel">
          <div className="form-content">
            <div className="form-header">
              <h2>Welcome Back</h2>
              <p>Sign in to continue to your dashboard</p>
            </div>

            {/* Quick Access Card */}
            <div className="quick-access-card">
              <div className="card-header">
                <span className="card-icon">üéØ</span>
                <span>Quick Access</span>
              </div>
              <p>Use demo credentials for instant access</p>
              <button 
                className="quick-fill-btn"
                onClick={fillDemoCredentials}
                type="button"
                disabled={isLoading}
              >
                <span className="btn-icon">‚ö°</span>
                Auto-Fill Demo
              </button>
            </div>

            {/* Login Form */}
            <form onSubmit={handleSubmit} className="login-form">
              {error && (
                <div className="error-message">
                  <span className="error-icon">‚ö†Ô∏è</span>
                  {error}
                </div>
              )}

              <div className="form-group">
                <label htmlFor="username">Username</label>
                <div className="input-container">
                  <span className="input-icon">üë§</span>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={credentials.username}
                    onChange={handleChange}
                    placeholder="Enter your username"
                    required
                    disabled={isLoading}
                    className={credentials.username ? 'has-value' : ''}
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <div className="input-container">
                  <span className="input-icon">üîë</span>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={credentials.password}
                    onChange={handleChange}
                    placeholder="Enter your password"
                    required
                    disabled={isLoading}
                    className={credentials.password ? 'has-value' : ''}
                  />
                </div>
              </div>

              <div className="form-options">
                <label className="checkbox-container">
                  <input
                    type="checkbox"
                    checked={rememberMe}
                    onChange={(e) => setRememberMe(e.target.checked)}
                    disabled={isLoading}
                  />
                  <span className="checkmark"></span>
                  Remember this device
                </label>
                <a href="#forgot" className="forgot-link">Forgot Password?</a>
              </div>
              
              <button 
                type="submit" 
                className={`login-btn ${isLoading ? 'loading' : ''}`}
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="spinner"></span>
                    Signing In...
                  </>
                ) : (
                  <>
                    <span className="btn-icon">‚Üí</span>
                    Sign In
                  </>
                )}
              </button>
            </form>

            {/* Demo Credentials */}
            <div className="demo-section">
              <div className="demo-header">
                <span className="demo-icon">üí°</span>
                <span>Demo Credentials</span>
              </div>
              <div className="credentials-grid">
                <div className="credential">
                  <span className="cred-label">Username</span>
                  <code className="cred-value">admin</code>
                </div>
                <div className="credential">
                  <span className="cred-label">Password</span>
                  <code className="cred-value">admin123</code>
                </div>
              </div>
            </div>

            {/* Footer */}
            <div className="login-footer">
              <div className="security-badge">
                <span className="security-icon">üõ°Ô∏è</span>
                Secured by ZMO ‚Ä¢ v2.1.0
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
