import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import '../components/auth/Login.css';

export default function LoginPage() {
  const { login } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [networkStatus, setNetworkStatus] = useState('');
  const [serverStatus, setServerStatus] = useState('unknown');

  // Check server status on component mount
  useEffect(() => {
    checkServerStatus();
  }, []);

  const checkServerStatus = async () => {
    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
    
    try {
      setNetworkStatus('Checking server status...');
      
      // First, try a simple connection test
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);
      
      const response = await fetch(`${apiUrl}/health`, {
        method: 'GET',
        signal: controller.signal,
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      clearTimeout(timeoutId);
      
      if (response.ok) {
        setServerStatus('running');
        setNetworkStatus('‚úÖ Server is running and accessible');
        return true;
      } else {
        setServerStatus('error');
        setNetworkStatus('‚ö†Ô∏è Server responded with error');
        return false;
      }
    } catch (error) {
      setServerStatus('down');
      
      if (error.name === 'AbortError') {
        setNetworkStatus('‚ùå Server timeout - backend might not be running');
      } else {
        setNetworkStatus('‚ùå Cannot connect to backend server');
      }
      
      return false;
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setNetworkStatus('');
    setLoading(true);

    try {
      // Validate inputs
      if (!email || !password) {
        throw new Error('Please enter both email and password');
      }

      // Check server status first
      setNetworkStatus('Verifying server connection...');
      const isServerRunning = await checkServerStatus();
      
      if (!isServerRunning) {
        throw new Error('Backend server is not accessible. Please make sure it\'s running on localhost:5000');
      }

      // Attempt login with progress updates
      setNetworkStatus('Attempting login...');
      console.log('Attempting login with:', { email, serverStatus });
      
      const data = await login(email, password);
      
      console.log('Login success:', data);
      setNetworkStatus('‚úÖ Login successful! Redirecting...');

      // Redirect after short delay
      setTimeout(() => {
        window.location.href = '/';
      }, 1000);

    } catch (err) {
      console.error('Login process error:', err);
      
      let errorMessage = 'Login failed. Please check your credentials.';
      let networkStatusMessage = 'Login failed';
      
      if (err.message.includes('timeout') || err.message.includes('Timeout')) {
        errorMessage = 'Server timeout: The backend is running but not responding to login requests. Check your backend logs.';
        networkStatusMessage = '‚ö†Ô∏è Server timeout - check backend';
      } else if (err.message.includes('Backend server is not accessible')) {
        errorMessage = 'Backend server is not running or not accessible. Please start your backend server.';
        networkStatusMessage = '‚ùå Server not running';
      } else if (err.message.includes('net::') || err.message.includes('Network Error')) {
        errorMessage = 'Network connection error. Check if backend is running on localhost:5000.';
        networkStatusMessage = 'üåê Network error';
      } else if (err.message.includes('credentials') || err.message.includes('401')) {
        errorMessage = 'Invalid email or password. Please try again.';
        networkStatusMessage = 'üîê Authentication failed';
      } else {
        errorMessage = err.message || errorMessage;
      }
      
      setError(errorMessage);
      setNetworkStatus(networkStatusMessage);
    } finally {
      setLoading(false);
    }
  };

  // Development helper
  const fillTestCredentials = () => {
    setEmail('admin@example.com');
    setPassword('password123');
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>Admin Login</h1>
        
        {/* Server status indicator */}
        <div className={`server-status ${serverStatus}`}>
          Server Status: 
          {serverStatus === 'running' && ' ‚úÖ Running'}
          {serverStatus === 'down' && ' ‚ùå Not Responding'}
          {serverStatus === 'error' && ' ‚ö†Ô∏è Error'}
          {serverStatus === 'unknown' && ' üîÑ Checking...'}
        </div>

        {/* Network status */}
        {networkStatus && (
          <div className={`network-status ${error ? 'error' : ''}`}>
            {networkStatus}
          </div>
        )}
        
        {/* Error message */}
        {error && <div className="error-message">{error}</div>}
        
        {/* Debug helper - remove in production */}
        {process.env.NODE_ENV === 'development' && (
          <div className="debug-hint">
            <button 
              type="button" 
              className="debug-btn"
              onClick={fillTestCredentials}
              disabled={loading}
            >
              Fill Test Credentials
            </button>
            <button 
              type="button" 
              className="debug-btn"
              onClick={checkServerStatus}
              disabled={loading}
            >
              Recheck Server
            </button>
          </div>
        )}

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
              placeholder="admin@example.com"
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
              placeholder="Enter password"
            />
          </div>

          <button 
            className={`login-btn ${loading ? 'loading' : ''}`} 
            type="submit" 
            disabled={loading || serverStatus === 'down'}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Authenticating...
              </>
            ) : (
              'Login'
            )}
          </button>
        </form>

        {/* Backend troubleshooting guide */}
        <div className="troubleshooting">
          <h3>Backend Connection Timeout?</h3>
          <p><strong>Issue:</strong> Server is reachable but login endpoint times out</p>
          <ol>
            <li><strong>Check backend logs</strong> for errors when login is attempted</li>
            <li><strong>Verify the login route</strong> exists: <code>POST /api/auth/login</code></li>
            <li><strong>Check CORS configuration</strong> in your backend</li>
            <li><strong>Test the endpoint directly:</strong>
              <pre>
{`curl -X POST http://localhost:5000/api/auth/login \\
  -H "Content-Type: application/json" \\
  -d '{"email":"test@test.com","password":"test"}'`}
              </pre>
            </li>
            <li><strong>Restart your backend server</strong></li>
          </ol>
        </div>
      </div>
    </div>
  );
}
