import React, { useState } from 'react';
import { blogAPI } from '../services/api';

const BlogManager = ({ onBlogUpdate }) => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    excerpt: '',
    featuredImage: '',
    status: 'draft',
    tags: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContentChange = (content) => {
    setFormData(prev => ({
      ...prev,
      content,
      excerpt: content.substring(0, 150) + '...' // Auto-generate excerpt
    }));
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const imageFormData = new FormData();
    imageFormData.append('image', file);

    try {
      setLoading(true);
      const response = await blogAPI.uploadImage(imageFormData);
      setFormData(prev => ({
        ...prev,
        featuredImage: response.data.imageUrl
      }));
      setMessage('Image uploaded successfully!');
    } catch (error) {
      setMessage('Error uploading image: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const blogData = {
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      await blogAPI.createBlog(blogData);
      setMessage('Blog post created successfully!');
      setFormData({
        title: '',
        content: '',
        excerpt: '',
        featuredImage: '',
        status: 'draft',
        tags: ''
      });
      
      if (onBlogUpdate) {
        onBlogUpdate();
      }
    } catch (error) {
      setMessage('Error creating blog: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="blog-manager">
      <h2>Create New Blog Post</h2>
      
      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="blog-form">
        <div className="form-group">
          <label htmlFor="title">Title *</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="excerpt">Excerpt</label>
          <textarea
            id="excerpt"
            name="excerpt"
            value={formData.excerpt}
            onChange={handleChange}
            rows="3"
            placeholder="Brief description of the post..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="content">Content *</label>
          <textarea
            id="content"
            name="content"
            value={formData.content}
            onChange={(e) => handleContentChange(e.target.value)}
            rows="10"
            required
            placeholder="Write your blog content here..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="featuredImage">Featured Image</label>
          <input
            type="file"
            id="featuredImage"
            accept="image/*"
            onChange={handleImageUpload}
          />
          {formData.featuredImage && (
            <div className="image-preview">
              <img src={formData.featuredImage} alt="Preview" />
            </div>
          )}
        </div>

        <div className="form-group">
          <label htmlFor="tags">Tags</label>
          <input
            type="text"
            id="tags"
            name="tags"
            value={formData.tags}
            onChange={handleChange}
            placeholder="tag1, tag2, tag3"
          />
        </div>

        <div className="form-group">
          <label htmlFor="status">Status</label>
          <select
            id="status"
            name="status"
            value={formData.status}
            onChange={handleChange}
          >
            <option value="draft">Draft</option>
            <option value="published">Published</option>
          </select>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Publishing...' : 'Publish Blog Post'}
        </button>
      </form>
    </div>
  );
};

export default BlogManager;
