const API_BASE_URL = process.env.REACT_APP_API_URL || '';

// Helper function for authenticated requests
const authFetch = async (url, options = {}) => {
  const token = localStorage.getItem('adminToken');
  
  const config = {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
  };

  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }

  const response = await fetch(`${API_BASE_URL}${url}`, config);
  
  if (response.status === 401) {
    localStorage.removeItem('adminToken');
    window.location.href = '/admin-panel/login';
    return;
  }

  return response;
};

export const adminApi = {
  // Auth
  async login(credentials) {
    const response = await fetch(`${API_BASE_URL}/api/auth/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    });
    return await response.json();
  },

  // Blogs - updated for your API structure
  async getBlogs(page = 1, limit = 10) {
    const response = await authFetch(`/api/blog/admin/all?page=${page}&limit=${limit}`);
    return await response.json();
  },

  async createBlog(blogData) {
    // For file upload, you'll need to use FormData
    const formData = new FormData();
    formData.append('title', blogData.title);
    formData.append('content', blogData.content);
    formData.append('excerpt', blogData.excerpt);
    formData.append('tags', JSON.stringify(blogData.tags || []));
    formData.append('published', blogData.published.toString());
    
    if (blogData.featuredImage) {
      formData.append('featuredImage', blogData.featuredImage);
    }

    const response = await fetch(`${API_BASE_URL}/api/blog`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('adminToken')}`,
      },
      body: formData,
    });
    return await response.json();
  },

  async updateBlog(id, blogData) {
    const formData = new FormData();
    formData.append('title', blogData.title);
    formData.append('content', blogData.content);
    formData.append('excerpt', blogData.excerpt);
    formData.append('tags', JSON.stringify(blogData.tags || []));
    formData.append('published', blogData.published.toString());
    
    if (blogData.featuredImage && typeof blogData.featuredImage !== 'string') {
      formData.append('featuredImage', blogData.featuredImage);
    }

    const response = await fetch(`${API_BASE_URL}/api/blog/${id}`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('adminToken')}`,
      },
      body: formData,
    });
    return await response.json();
  },

  async deleteBlog(id) {
    const response = await authFetch(`/api/blog/${id}`, {
      method: 'DELETE',
    });
    return await response.json();
  },

  // Projects (similar structure)
  async getProjects() {
    const response = await authFetch('/api/projects');
    return await response.json();
  },

  async createProject(projectData) {
    const response = await authFetch('/api/projects', {
      method: 'POST',
      body: JSON.stringify(projectData),
    });
    return await response.json();
  },

  async updateProject(id, projectData) {
    const response = await authFetch(`/api/projects/${id}`, {
      method: 'PUT',
      body: JSON.stringify(projectData),
    });
    return await response.json();
  },

  async deleteProject(id) {
    const response = await authFetch(`/api/projects/${id}`, {
      method: 'DELETE',
    });
    return await response.json();
  },

  // Frontend Preview
  async getFrontendPreview() {
    const response = await authFetch('/api/frontend-preview');
    return await response.json();
  }
};
