import React, { useState } from 'react';
import './BlogManager.css';

const BlogManager = ({ blog, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: blog?.title || '',
    content: blog?.content || '',
    excerpt: blog?.excerpt || '',
    image: blog?.image || '',
    tags: blog?.tags?.join(', ') || '',
    published: blog?.published || false
  });
  const [imagePreview, setImagePreview] = useState(blog?.image || '');
  const [selectedFile, setSelectedFile] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      
      // Create preview URL
      const previewUrl = URL.createObjectURL(file);
      setImagePreview(previewUrl);
      
      // You can also set the image URL for form data if needed
      // For now, we'll keep the file separate and handle it in submit
    }
  };

  const handleUrlChange = (e) => {
    const url = e.target.value;
    setFormData({...formData, image: url});
    setImagePreview(url);
    setSelectedFile(null); // Clear file when URL is entered
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    let finalImageUrl = formData.image;
    
    // If a local file is selected, you would typically upload it here
    // For now, we'll use the preview URL or keep the existing URL
    if (selectedFile) {
      // In a real application, you would upload the file to your server
      // and get back the actual image URL
      // For demo purposes, we'll use the object URL (note: this is temporary)
      finalImageUrl = imagePreview;
      
      // Example of how you might handle file upload:
      // const uploadedUrl = await uploadImage(selectedFile);
      // finalImageUrl = uploadedUrl;
    }
    
    const blogData = {
      title: formData.title,
      content: formData.content,
      excerpt: formData.excerpt,
      image: finalImageUrl,
      tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
      published: formData.published
    };
    
    if (blog?._id) {
      blogData._id = blog._id;
    }
    
    onSave(blogData);
  };

  const clearImage = () => {
    setImagePreview('');
    setFormData({...formData, image: ''});
    setSelectedFile(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{blog ? 'Edit Blog' : 'Create New Blog'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Content</label>
            <textarea
              value={formData.content}
              onChange={(e) => setFormData({...formData, content: e.target.value})}
              rows="6"
              required
            />
          </div>

          <div className="form-group">
            <label>Excerpt</label>
            <textarea
              value={formData.excerpt}
              onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
              rows="3"
            />
          </div>

          {/* Image Section */}
          <div className="form-group">
            <label>Blog Image</label>
            
            {/* Image Preview */}
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" />
                <button type="button" className="clear-image-btn" onClick={clearImage}>
                  Remove Image
                </button>
              </div>
            )}

            {/* Local File Upload */}
            <div className="image-upload-section">
              <label className="upload-label">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="file-input"
                />
                <span className="upload-button">Choose Local Image</span>
              </label>
              <span className="upload-hint">or</span>
            </div>

            {/* URL Input */}
            <input
              type="url"
              value={formData.image}
              onChange={handleUrlChange}
              placeholder="Or enter image URL"
              className="url-input"
            />
            
            <div className="image-notes">
              <small>Supported formats: JPG, PNG, GIF, WebP â€¢ Max size: 5MB</small>
            </div>
          </div>

          <div className="form-group">
            <label>Tags (comma separated)</label>
            <input
              type="text"
              value={formData.tags}
              onChange={(e) => setFormData({...formData, tags: e.target.value})}
            />
          </div>

          <div className="form-group checkbox">
            <label>
              <input
                type="checkbox"
                checked={formData.published}
                onChange={(e) => setFormData({...formData, published: e.target.checked})}
              />
              Published
            </label>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary">
              {blog ? 'Update Blog' : 'Create Blog'}
            </button>
            <button type="button" className="btn-secondary" onClick={onCancel}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BlogManager;
