// admin-panel/src/services/api.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token expired or invalid
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Authentication API
export const authAPI = {
  // Admin login
  login: async (email, password) => {
    const response = await api.post('/admin/auth/login', { email, password });
    return response.data;
  },

  // Admin logout
  logout: async () => {
    const response = await api.post('/admin/auth/logout');
    return response.data;
  },

  // Get current admin user
  getCurrentUser: async () => {
    const response = await api.get('/admin/auth/me');
    return response.data;
  },

  // Refresh token
  refreshToken: async () => {
    const response = await api.post('/admin/auth/refresh');
    return response.data;
  },

  // Verify token
  verifyToken: async (token) => {
    const response = await api.post('/admin/auth/verify', { token });
    return response.data;
  }
};

// Dashboard API
export const dashboardAPI = {
  getStats: async () => {
    const response = await api.get('/admin/dashboard/stats');
    return response.data;
  },

  getRecentActivity: async () => {
    const response = await api.get('/admin/dashboard/activity');
    return response.data;
  },

  createTestBlog: async () => {
    const response = await api.post('/admin/blogs/test');
    return response.data;
  },

  createTestProject: async () => {
    const response = await api.post('/admin/projects/test');
    return response.data;
  },

  createTestMessage: async () => {
    const response = await api.post('/admin/contacts/test');
    return response.data;
  },

  publishBlog: async (blogId) => {
    const response = await api.put(`/admin/blogs/${blogId}/publish`);
    return response.data;
  },

  getPublishedBlogs: async () => {
    const response = await api.get('/blogs');
    return response.data;
  },
};

// Blog API
export const blogAPI = {
  getAll: async (page = 1, limit = 10) => {
    const response = await api.get(`/admin/blogs?page=${page}&limit=${limit}`);
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/admin/blogs/${id}`);
    return response.data;
  },

  create: async (blogData) => {
    const response = await api.post('/admin/blogs', blogData);
    return response.data;
  },

  update: async (id, blogData) => {
    const response = await api.put(`/admin/blogs/${id}`, blogData);
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/admin/blogs/${id}`);
    return response.data;
  },

  publish: async (id) => {
    const response = await api.put(`/admin/blogs/${id}/publish`);
    return response.data;
  },

  unpublish: async (id) => {
    const response = await api.put(`/admin/blogs/${id}/unpublish`);
    return response.data;
  },

  // Frontend API (no auth required)
  getPublished: async (page = 1, limit = 10) => {
    const response = await api.get(`/blogs/published?page=${page}&limit=${limit}`);
    return response.data;
  },

  getBySlug: async (slug) => {
    const response = await api.get(`/blogs/slug/${slug}`);
    return response.data;
  },

  getFeatured: async () => {
    const response = await api.get('/blogs/featured');
    return response.data;
  },

  getCategories: async () => {
    const response = await api.get('/blogs/categories');
    return response.data;
  },
};

// Project API
export const projectAPI = {
  getAll: async (page = 1, limit = 10) => {
    const response = await api.get(`/admin/projects?page=${page}&limit=${limit}`);
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/admin/projects/${id}`);
    return response.data;
  },

  create: async (projectData) => {
    const response = await api.post('/admin/projects', projectData);
    return response.data;
  },

  update: async (id, projectData) => {
    const response = await api.put(`/admin/projects/${id}`, projectData);
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/admin/projects/${id}`);
    return response.data;
  },

  // Frontend API
  getPublished: async (page = 1, limit = 10) => {
    const response = await api.get(`/projects?page=${page}&limit=${limit}`);
    return response.data;
  },

  getFeatured: async () => {
    const response = await api.get('/projects/featured');
    return response.data;
  },
};

// Contact API
export const contactAPI = {
  getAll: async (page = 1, limit = 10) => {
    const response = await api.get(`/admin/contacts?page=${page}&limit=${limit}`);
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/admin/contacts/${id}`);
    return response.data;
  },

  markAsRead: async (id) => {
    const response = await api.put(`/admin/contacts/${id}/read`);
    return response.data;
  },

  markAsUnread: async (id) => {
    const response = await api.put(`/admin/contacts/${id}/unread`);
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/admin/contacts/${id}`);
    return response.data;
  },

  // Public contact form submission
  submit: async (contactData) => {
    const response = await api.post('/contacts', contactData);
    return response.data;
  },
};

// File Upload API
export const uploadAPI = {
  uploadImage: async (file) => {
    const formData = new FormData();
    formData.append('image', file);
    
    const response = await api.post('/admin/upload/image', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },

  uploadFile: async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    
    const response = await api.post('/admin/upload/file', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },

  deleteFile: async (filePath) => {
    const response = await api.delete('/admin/upload/file', {
      data: { filePath }
    });
    return response.data;
  }
};

export default api;
