import React, { useState, useEffect } from 'react';
import BlogManager from '../components/BlogManager';
import { adminAPI } from '../services/adminAPI';
import './BlogsPage.css';

const BlogsPage = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingBlog, setEditingBlog] = useState(null);
  const [error, setError] = useState('');

  const fetchBlogs = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getBlogs();
      if (response.data.success) {
        setBlogs(response.data.data);
        setError('');
      } else {
        setError(response.data.message || 'Failed to load blogs');
      }
    } catch (err) {
      console.error('Error fetching blogs:', err);
      setError('Failed to load blogs: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteBlog = async (blogId) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        const response = await adminAPI.deleteBlog(blogId);
        if (response.data.success) {
          alert('Blog deleted successfully!');
          fetchBlogs();
        }
      } catch (error) {
        alert('Error deleting blog: ' + error.message);
      }
    }
  };

  const handleSaveBlog = async (blogData) => {
    try {
      let response;
      if (blogData._id) {
        response = await adminAPI.updateBlog(blogData._id, blogData);
      } else {
        response = await adminAPI.createBlog(blogData);
      }
      
      if (response.data.success) {
        alert('Blog saved successfully!');
        setShowForm(false);
        setEditingBlog(null);
        fetchBlogs();
      }
    } catch (error) {
      alert('Error saving blog: ' + error.message);
    }
  };

  useEffect(() => {
    fetchBlogs();
  }, []);

  if (loading) return <div>Loading blogs...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Blog Management</h1>
        <button onClick={() => setShowForm(true)} className="btn-primary">
          + Add New Blog
        </button>
      </div>

      {showForm && (
        <BlogManager
          blog={editingBlog}
          onSave={handleSaveBlog}
          onCancel={() => {
            setShowForm(false);
            setEditingBlog(null);
          }}
        />
      )}

      <div className="blogs-list">
        {blogs.map(blog => (
          <div key={blog._id} className="blog-card">
            <h3>{blog.title}</h3>
            <p>{blog.excerpt}</p>
            <div className="blog-actions">
              <button 
                onClick={() => {
                  setEditingBlog(blog);
                  setShowForm(true);
                }}
                className="btn-edit"
              >
                Edit
              </button>
              <button 
                onClick={() => handleDeleteBlog(blog._id)}
                className="btn-delete"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogsPage;
