import React, { useState, useEffect } from 'react';
import BlogManager from '../components/BlogManager';
import { blogsAPI, checkBackendConnection } from '../services/api';

const BlogsPage = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');

  const fetchBlogs = async () => {
    try {
      setLoading(true);
      setError(null);
      setBackendStatus('checking');
      
      // First check if backend is available
      const isBackendAvailable = await checkBackendConnection();
      if (!isBackendAvailable) {
        throw new Error('Cannot connect to backend server. Make sure it\'s running on port 5001.');
      }
      
      setBackendStatus('connected');
      
      // Fetch blogs from API
      console.log('üîÑ Fetching blogs from API...');
      const response = await blogsAPI.getBlogs();
      
      // Handle different response formats
      let blogsData = [];
      if (Array.isArray(response)) {
        blogsData = response;
      } else if (response.data && Array.isArray(response.data)) {
        blogsData = response.data;
      } else if (response.blogs && Array.isArray(response.blogs)) {
        blogsData = response.blogs;
      } else if (response.success !== false) {
        // If it's a successful response but format is unexpected
        blogsData = response;
      }
      
      console.log(`‚úÖ Loaded ${blogsData.length} blogs`);
      setBlogs(Array.isArray(blogsData) ? blogsData : []);
      
    } catch (err) {
      console.error('‚ùå Error fetching blogs:', err);
      setError(err.message);
      setBackendStatus('disconnected');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBlogs();
  }, []);

  const handleRetry = () => {
    fetchBlogs();
  };

  const handleBlogsUpdate = (updatedBlogs) => {
    setBlogs(updatedBlogs);
  };

  // Show loading state
  if (loading && blogs.length === 0) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingContent}>
          <div style={styles.spinner}></div>
          <div>Loading blogs...</div>
          {backendStatus === 'checking' && (
            <div style={styles.statusMessage}>Checking backend connection...</div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div style={styles.pageContainer}>
      <div style={styles.pageHeader}>
        <h1>Blog Management</h1>
        <p>Create, edit, and manage your blog posts</p>
        <div style={styles.statusIndicator}>
          Backend Status: 
          <span style={
            backendStatus === 'connected' ? styles.statusConnected : 
            backendStatus === 'disconnected' ? styles.statusDisconnected : 
            styles.statusChecking
          }>
            {backendStatus === 'connected' ? ' ‚úÖ Connected' : 
             backendStatus === 'disconnected' ? ' ‚ùå Disconnected' : 
             ' üîÑ Checking...'}
          </span>
        </div>
      </div>
      
      <BlogManager 
        blogs={blogs}
        onBlogsUpdate={handleBlogsUpdate}
        error={error}
        onRetry={handleRetry}
      />
    </div>
  );
};

const styles = {
  pageContainer: {
    padding: '20px',
    backgroundColor: '#f8f9fa',
    minHeight: '100vh'
  },
  pageHeader: {
    marginBottom: '30px',
    textAlign: 'center'
  },
  statusIndicator: {
    marginTop: '10px',
    fontSize: '14px',
    color: '#666'
  },
  statusConnected: {
    color: '#28a745',
    fontWeight: 'bold'
  },
  statusDisconnected: {
    color: '#dc3545',
    fontWeight: 'bold'
  },
  statusChecking: {
    color: '#ffc107',
    fontWeight: 'bold'
  },
  loadingContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '60vh',
    flexDirection: 'column'
  },
  loadingContent: {
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '15px'
  },
  spinner: {
    width: '40px',
    height: '40px',
    border: '4px solid #f3f3f3',
    borderTop: '4px solid #007bff',
    borderRadius: '50%',
    animation: 'spin 1s linear infinite'
  },
  statusMessage: {
    fontSize: '14px',
    color: '#666',
    fontStyle: 'italic'
  }
};

// Add CSS animation for spinner
const spinnerStyle = document.createElement('style');
spinnerStyle.textContent = `
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;
document.head.appendChild(spinnerStyle);

export default BlogsPage;
