import React, { useState } from 'react';
import './ProjectManager.css';

const ProjectManager = ({ project, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: project?.name || '',
    description: project?.description || '',
    technologies: project?.technologies?.join(', ') || '',
    githubUrl: project?.githubUrl || '',
    liveUrl: project?.liveUrl || '',
    image: project?.image || '',
    featured: project?.featured || false
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const projectData = {
      name: formData.name,
      description: formData.description,
      technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech),
      githubUrl: formData.githubUrl,
      liveUrl: formData.liveUrl,
      image: formData.image,
      featured: formData.featured
    };
    
    console.log('ðŸ“¦ Sending project data to backend:', projectData);
    onSave(projectData);
  };

  // âœ… KEEPING AND USING the image upload function
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData(prev => ({ 
          ...prev, 
          image: reader.result // Store base64 image data
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{project ? 'Edit Project' : 'Create New Project'}</h2>
        <form onSubmit={handleSubmit}>
          {/* ... other form fields ... */}

          <div className="form-group">
            <label>Project Image</label>
            
            <div className="image-upload-section">
              <label className="upload-label">Upload Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload} // âœ… USING the function
                className="file-input"
              />
            </div>

            <div className="image-url-section">
              <label className="upload-label">Or use image URL:</label>
              <input
                type="url"
                value={formData.image}
                onChange={(e) => setFormData({...formData, image: e.target.value})}
                placeholder="https://example.com/project-image.jpg"
                className="url-input"
              />
            </div>

            {formData.image && (
              <div className="image-preview">
                <p>Image Preview:</p>
                <img 
                  src={formData.image} 
                  alt="Preview" 
                  className="preview-image"
                />
              </div>
            )}
          </div>

          {/* ... rest of form fields ... */}
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
