import React, { useState, useEffect } from 'react';
import { adminProjectAPI } from '../services/api';

const ProjectManager = () => {
    const [projects, setProjects] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [editingProject, setEditingProject] = useState(null);
    const [loading, setLoading] = useState(false);
    const [formLoading, setFormLoading] = useState(false);
    
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        technologies: '',
        projectUrl: '',
        githubUrl: '',
        featured: false,
        status: 'completed',
        featuredImage: null
    });

    useEffect(() => {
        fetchProjects();
    }, []);

    const fetchProjects = async () => {
        setLoading(true);
        try {
            const response = await adminProjectAPI.getAll();
            setProjects(response.data);
        } catch (error) {
            console.error('Error fetching projects:', error);
            alert('Error fetching projects');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormLoading(true);

        try {
            const submitData = {
                ...formData,
                technologies: formData.technologies.split(',').map(tech => tech.trim()).filter(tech => tech)
            };

            if (editingProject) {
                await adminProjectAPI.update(editingProject._id, submitData);
            } else {
                await adminProjectAPI.create(submitData);
            }

            resetForm();
            fetchProjects();
            alert(editingProject ? 'Project updated successfully!' : 'Project created successfully!');
        } catch (error) {
            console.error('Error saving project:', error);
            alert(error.response?.data?.message || 'Error saving project');
        } finally {
            setFormLoading(false);
        }
    };

    const handleEdit = (project) => {
        setEditingProject(project);
        setFormData({
            title: project.title,
            description: project.description,
            technologies: project.technologies.join(', '),
            projectUrl: project.projectUrl || '',
            githubUrl: project.githubUrl || '',
            featured: project.featured,
            status: project.status,
            featuredImage: null
        });
        setShowForm(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this project?')) {
            try {
                await adminProjectAPI.delete(id);
                fetchProjects();
                alert('Project deleted successfully!');
            } catch (error) {
                console.error('Error deleting project:', error);
                alert('Error deleting project');
            }
        }
    };

    const resetForm = () => {
        setFormData({
            title: '',
            description: '',
            technologies: '',
            projectUrl: '',
            githubUrl: '',
            featured: false,
            status: 'completed',
            featuredImage: null
        });
        setEditingProject(null);
        setShowForm(false);
    };

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : 
                    type === 'file' ? files[0] : value
        });
    };

    return (
        <div className="project-manager">
            <div className="header">
                <h2>Project Management</h2>
                <button onClick={() => setShowForm(true)} className="btn-primary">
                    Add New Project
                </button>
            </div>

            {showForm && (
                <div className="form-modal">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h3>{editingProject ? 'Edit Project' : 'Create Project'}</h3>
                            <button onClick={resetForm} className="close-btn">&times;</button>
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>Title *</label>
                                <input
                                    type="text"
                                    name="title"
                                    value={formData.title}
                                    onChange={handleChange}
                                    required
                                    placeholder="Enter project title"
                                />
                            </div>
                            <div className="form-group">
                                <label>Description *</label>
                                <textarea
                                    name="description"
                                    value={formData.description}
                                    onChange={handleChange}
                                    required
                                    placeholder="Describe your project..."
                                    rows="4"
                                />
                            </div>
                            <div className="form-group">
                                <label>Technologies</label>
                                <input
                                    type="text"
                                    name="technologies"
                                    value={formData.technologies}
                                    onChange={handleChange}
                                    placeholder="React, Node.js, MongoDB"
                                />
                                <small>Separate technologies with commas</small>
                            </div>
                            <div className="form-group">
                                <label>Project URL</label>
                                <input
                                    type="url"
                                    name="projectUrl"
                                    value={formData.projectUrl}
                                    onChange={handleChange}
                                    placeholder="https://your-project.com"
                                />
                            </div>
                            <div className="form-group">
                                <label>GitHub URL</label>
                                <input
                                    type="url"
                                    name="githubUrl"
                                    value={formData.githubUrl}
                                    onChange={handleChange}
                                    placeholder="https://github.com/your-repo"
                                />
                            </div>
                            <div className="form-group">
                                <label>Featured Image</label>
                                <input
                                    type="file"
                                    name="featuredImage"
                                    onChange={handleChange}
                                    accept="image/*"
                                />
                            </div>
                            <div className="form-group">
                                <label>Status</label>
                                <select
                                    name="status"
                                    value={formData.status}
                                    onChange={handleChange}
                                >
                                    <option value="completed">Completed</option>
                                    <option value="in-progress">In Progress</option>
                                    <option value="planned">Planned</option>
                                </select>
                            </div>
                            <div className="form-group checkbox">
                                <label>
                                    <input
                                        type="checkbox"
                                        name="featured"
                                        checked={formData.featured}
                                        onChange={handleChange}
                                    />
                                    Featured Project
                                </label>
                            </div>
                            <div className="form-actions">
                                <button type="submit" disabled={formLoading} className="btn-primary">
                                    {formLoading ? 'Saving...' : (editingProject ? 'Update Project' : 'Create Project')}
                                </button>
                                <button type="button" onClick={resetForm} className="btn-secondary">
                                    Cancel
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            <div className="projects-list">
                {loading ? (
                    <div className="loading">Loading projects...</div>
                ) : (
                    <table className="data-table">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Status</th>
                                <th>Featured</th>
                                <th>Technologies</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {projects.map(project => (
                                <tr key={project._id}>
                                    <td>
                                        <div className="project-title-cell">
                                            <strong>{project.title}</strong>
                                            {project.featuredImage && (
                                                <small>Has image</small>
                                            )}
                                        </div>
                                    </td>
                                    <td>
                                        <span className={`status ${project.status}`}>
                                            {project.status}
                                        </span>
                                    </td>
                                    <td>
                                        {project.featured ? (
                                            <span className="featured-badge">‚≠ê Featured</span>
                                        ) : (
                                            <span className="not-featured">No</span>
                                        )}
                                    </td>
                                    <td>
                                        <div className="technologies-list">
                                            {project.technologies.slice(0, 3).map(tech => (
                                                <span key={tech} className="tech-tag">{tech}</span>
                                            ))}
                                            {project.technologies.length > 3 && (
                                                <span className="more-tags">+{project.technologies.length - 3} more</span>
                                            )}
                                        </div>
                                    </td>
                                    <td className="actions">
                                        <button 
                                            onClick={() => handleEdit(project)} 
                                            className="btn-edit"
                                        >
                                            Edit
                                        </button>
                                        <button 
                                            onClick={() => handleDelete(project._id)} 
                                            className="btn-delete"
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default ProjectManager;
