// admin-panel/src/services/api.js - UPDATED FOR ACTUAL BACKEND ENDPOINTS
import axios from 'axios';

// Use your live backend URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'https://zmo-backend.vercel.app/api';
console.log('üîß Live API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
  timeout: 15000, // 15 second timeout for live API
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('adminToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  console.log(`üöÄ ${config.method?.toUpperCase()} Request to: ${config.url}`);
  return config;
});

// Handle responses globally
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ ${response.status} Response from: ${response.config.url}`, response.data);
    return response;
  },
  (error) => {
    console.error(`‚ùå API Error:`, {
      url: error.config?.url,
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    
    if (error.response?.status === 401) {
      localStorage.removeItem('adminToken');
      localStorage.removeItem('adminUser');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// --- Authentication API ---
export const authAPI = {
  login: async (email, password) => {
    try {
      console.log('üîê Attempting login to:', `${API_BASE_URL}/auth/login`);
      const res = await api.post('/auth/login', { email, password });
      console.log('‚úÖ Login successful:', res.data);
      return res.data;
    } catch (error) {
      console.error('‚ùå Login failed:', error.response?.data || error.message);
      throw error;
    }
  },
  
  logout: async () => {
    // Clear local storage for logout
    localStorage.removeItem('adminToken');
    localStorage.removeItem('adminUser');
    return { message: 'Logout successful' };
  },
  
  verify: async () => {
    try {
      const res = await api.get('/auth/verify');
      return res.data;
    } catch (error) {
      console.error('‚ùå Token verification failed:', error);
      throw error;
    }
  },
};

// --- Dashboard API ---
export const dashboardAPI = {
  getStats: async () => {
    try {
      const res = await api.get('/admin/dashboard/stats');
      return res.data;
    } catch (error) {
      console.error('‚ùå Error fetching stats:', error);
      // Return mock data if API fails
      return {
        stats: {
          totalBlogs: 0,
          publishedBlogs: 0,
          draftBlogs: 0,
          totalProjects: 0,
          totalContacts: 0,
          totalUsers: 0,
          monthlyGrowth: 0,
          activeUsers: 0
        }
      };
    }
  },
  
  getRecentActivity: async () => {
    try {
      const res = await api.get('/admin/dashboard/recent');
      return res.data;
    } catch (error) {
      console.error('‚ùå Error fetching recent activity:', error);
      return { activities: [] };
    }
  },
};

// --- Blog API --- (UPDATED FOR ACTUAL ENDPOINTS)
export const blogAPI = {
  getAll: async (page = 1, limit = 50) => {
    try {
      console.log('üì° Fetching blogs from:', `${API_BASE_URL}/admin/blogs`);
      const res = await api.get(`/admin/blogs?page=${page}&limit=${limit}`);
      console.log('‚úÖ Blogs response:', res.data);
      
      // Handle response structure
      const responseData = res.data;
      
      if (responseData && Array.isArray(responseData.blogs)) {
        return responseData.blogs;
      }
      
      if (responseData && Array.isArray(responseData.data)) {
        return responseData.data;
      }
      
      if (Array.isArray(responseData)) {
        return responseData;
      }
      
      console.warn('‚ö†Ô∏è Unexpected blog response format:', responseData);
      return [];
    } catch (error) {
      console.error('‚ùå Error fetching blogs:', error);
      throw error;
    }
  },
  
  create: async (data) => {
    try {
      console.log('üìù Creating blog with data:', data);
      
      // Prepare data for backend
      const blogData = {
        title: data.title,
        content: data.content,
        imageUrl: data.imageUrl || '',
        tags: data.tags || ['general'],
        excerpt: data.excerpt || data.content?.substring(0, 150) + '...'
      };
      
      const res = await api.post('/admin/blogs', blogData);
      console.log('‚úÖ Blog created successfully:', res.data);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error creating blog:', error);
      throw error;
    }
  },
  
  update: async (id, data) => {
    try {
      console.log('üìù Updating blog:', id, data);
      
      const blogData = {
        title: data.title,
        content: data.content,
        imageUrl: data.imageUrl || '',
        tags: data.tags || ['general'],
        excerpt: data.excerpt || data.content?.substring(0, 150) + '...',
        published: data.published !== undefined ? data.published : true
      };
      
      const res = await api.put(`/admin/blogs/${id}`, blogData);
      console.log('‚úÖ Blog updated successfully:', res.data);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error updating blog:', error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      console.log('üóëÔ∏è Deleting blog:', id);
      const res = await api.delete(`/admin/blogs/${id}`);
      console.log('‚úÖ Blog deleted successfully:', res.data);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error deleting blog:', error);
      throw error;
    }
  },
  
  getById: async (id) => {
    try {
      const res = await api.get(`/admin/blogs/${id}`);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error fetching blog by ID:', error);
      throw error;
    }
  },
};

// --- Project API ---
export const projectAPI = {
  getAll: async (page = 1, limit = 50) => {
    try {
      const res = await api.get(`/admin/projects?page=${page}&limit=${limit}`);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error fetching projects:', error);
      return { projects: [] };
    }
  },
  
  create: async (data) => {
    try {
      const res = await api.post('/admin/projects', data);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error creating project:', error);
      throw error;
    }
  },
  
  update: async (id, data) => {
    try {
      const res = await api.put(`/admin/projects/${id}`, data);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error updating project:', error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      const res = await api.delete(`/admin/projects/${id}`);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error deleting project:', error);
      throw error;
    }
  },
};

// --- Contact/Message API ---
export const contactAPI = {
  getAll: async (page = 1, limit = 50) => {
    try {
      const res = await api.get(`/admin/contacts?page=${page}&limit=${limit}`);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error fetching contacts:', error);
      return { contacts: [] };
    }
  },
  
  getById: async (id) => {
    try {
      const res = await api.get(`/admin/contacts/${id}`);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error fetching contact:', error);
      throw error;
    }
  },
  
  updateStatus: async (id, status) => {
    try {
      const res = await api.patch(`/admin/contacts/${id}`, { status });
      return res.data;
    } catch (error) {
      console.error('‚ùå Error updating contact status:', error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      const res = await api.delete(`/admin/contacts/${id}`);
      return res.data;
    } catch (error) {
      console.error('‚ùå Error deleting contact:', error);
      throw error;
    }
  },
};

// --- File Upload API ---
export const uploadAPI = {
  uploadImage: async (file) => {
    try {
      const formData = new FormData();
      formData.append('image', file);
      
      const res = await api.post('/admin/upload/image', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      return res.data;
    } catch (error) {
      console.error('‚ùå Error uploading image:', error);
      // Return mock image URL if upload fails
      return {
        url: `https://picsum.photos/800/600?random=${Date.now()}`,
        message: 'Using mock image URL'
      };
    }
  },
};

// Health check function
export const checkAPIHealth = async () => {
  try {
    const res = await api.get('/health');
    return res.data;
  } catch (error) {
    console.error('‚ùå API Health check failed:', error);
    throw error;
  }
};

export default api;
