import React, { useState, useEffect } from 'react';
import ProjectManager from './ProjectManager';
import { projectsAPI } from '../services/api';

const AdminProjects = () => {
  const [projects, setProjects] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingProject, setEditingProject] = useState(null);

  const fetchProjects = async () => {
    try {
      const result = await projectsAPI.getAllProjects();
      if (result.success) {
        setProjects(result.data);
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  // âœ… ADD THIS DELETE FUNCTION
  const handleDeleteProject = async (projectId) => {
    console.log('ðŸ—‘ï¸ Deleting project:', projectId);
    
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        const result = await projectsAPI.deleteProject(projectId);
        console.log('ðŸ“¦ Delete response:', result);
        
        if (result.success) {
          alert('Project deleted successfully!');
          fetchProjects(); // Refresh the list
        } else {
          alert('Failed to delete project: ' + result.message);
        }
      } catch (error) {
        console.error('âŒ Delete error:', error);
        alert('Error deleting project: ' + error.message);
      }
    }
  };

  const handleSaveProject = async (projectData) => {
    try {
      if (projectData._id) {
        // Update existing project
        await projectsAPI.updateProject(projectData._id, projectData);
      } else {
        // Create new project
        await projectsAPI.createProject(projectData);
      }
      setShowForm(false);
      setEditingProject(null);
      fetchProjects(); // Refresh list
    } catch (error) {
      alert('Error saving project: ' + error.message);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  return (
    <div className="admin-projects">
      <div className="header">
        <h2>Project Management</h2>
        <button onClick={() => setShowForm(true)} className="btn-primary">
          Add New Project
        </button>
      </div>

      {showForm && (
        <ProjectManager
          project={editingProject}
          onSave={handleSaveProject}
          onCancel={() => {
            setShowForm(false);
            setEditingProject(null);
          }}
        />
      )}

      <div className="project-list">
        {projects.map(project => (
          <div key={project._id} className="project-card">
            <div className="project-info">
              <h3>{project.name}</h3>
              <p>{project.description}</p>
              <div className="technologies">
                {project.technologies?.map((tech, index) => (
                  <span key={index} className="tech-tag">{tech}</span>
                ))}
              </div>
            </div>
            
            <div className="project-actions">
              <button 
                onClick={() => {
                  setEditingProject(project);
                  setShowForm(true);
                }}
                className="btn-edit"
              >
                Edit
              </button>
              
              {/* âœ… ADD DELETE BUTTON HERE */}
              <button 
                onClick={() => handleDeleteProject(project._id)}
                className="btn-delete"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminProjects;
