import React, { useState } from 'react';
import './BlogManager.css';

const BlogManager = ({ blog, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: blog?.title || '',
    content: blog?.content || '',
    excerpt: blog?.excerpt || '',
    image: blog?.image || '',
    tags: blog?.tags?.join(', ') || '',
    published: blog?.published || false
  });
  const [imagePreview, setImagePreview] = useState(blog?.image || '');
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file
      if (file.size > 5 * 1024 * 1024) {
        alert('File size must be less than 5MB');
        return;
      }
      if (!file.type.startsWith('image/')) {
        alert('Please select an image file');
        return;
      }

      setSelectedFile(file);
      const previewUrl = URL.createObjectURL(file);
      setImagePreview(previewUrl);
    }
  };

  const handleUrlChange = (e) => {
    const url = e.target.value;
    setFormData({...formData, image: url});
    setImagePreview(url);
    setSelectedFile(null);
  };

  // âœ… FIXED: Upload function
  const uploadImageToServer = async (file) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      console.log('ðŸš€ Uploading file to server...');
      
      const response = await fetch('http://localhost:5000/api/upload/image', {
        method: 'POST',
        body: formData,
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`Upload failed with status: ${response.status}`);
      }

      const data = await response.json();
      
      if (!data.success) {
        throw new Error(data.error || 'Upload failed');
      }

      console.log('âœ… Upload successful. Image URL:', data.imageUrl);
      return data.imageUrl; // This now returns full URL from backend

    } catch (error) {
      console.error('âŒ Upload error:', error);
      throw new Error(`Upload failed: ${error.message}`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // If no local file, save with existing URL
    if (!selectedFile) {
      const blogData = {
        title: formData.title,
        content: formData.content,
        excerpt: formData.excerpt,
        image: formData.image,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        published: formData.published
      };
      
      if (blog?._id) blogData._id = blog._id;
      onSave(blogData);
      return;
    }

    // If local file selected, upload it
    setUploading(true);

    try {
      console.log('ðŸ“¤ Starting image upload...');
      const imageUrl = await uploadImageToServer(selectedFile);
      console.log('ðŸŽ‰ Image uploaded. URL:', imageUrl);
      
      // Clean up temporary URL
      if (imagePreview.startsWith('blob:')) {
        URL.revokeObjectURL(imagePreview);
      }
      
      const blogData = {
        title: formData.title,
        content: formData.content,
        excerpt: formData.excerpt,
        image: imageUrl, // This is now full URL like "http://localhost:5000/uploads/filename.jpg"
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        published: formData.published
      };
      
      if (blog?._id) blogData._id = blog._id;
      
      console.log('ðŸ’¾ Saving blog with image:', blogData.image);
      onSave(blogData);
      
    } catch (error) {
      alert(`Upload Error: ${error.message}`);
      console.error('Submit error:', error);
    } finally {
      setUploading(false);
    }
  };

  const clearImage = () => {
    if (imagePreview.startsWith('blob:')) {
      URL.revokeObjectURL(imagePreview);
    }
    setImagePreview('');
    setFormData({...formData, image: ''});
    setSelectedFile(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{blog ? 'Edit Blog' : 'Create New Blog'}</h2>
        <form onSubmit={handleSubmit}>
          {/* Form fields remain the same */}
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Content</label>
            <textarea
              value={formData.content}
              onChange={(e) => setFormData({...formData, content: e.target.value})}
              rows="6"
              required
              disabled={uploading}
            />
          </div>

          <div className="form-group">
            <label>Excerpt</label>
            <textarea
              value={formData.excerpt}
              onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
              rows="3"
              disabled={uploading}
            />
          </div>

          {/* Image Section */}
          <div className="form-group">
            <label>Blog Image</label>
            
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" />
                <button type="button" className="clear-image-btn" onClick={clearImage}>
                  Remove Image
                </button>
              </div>
            )}

            <div className="image-upload-section">
              <label className="upload-label">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="file-input"
                  disabled={uploading}
                />
                <span className={`upload-button ${uploading ? 'uploading' : ''}`}>
                  {uploading ? 'Uploading...' : 'Choose Local Image'}
                </span>
              </label>
              <span className="upload-hint">or</span>
            </div>

            <input
              type="url"
              value={formData.image}
              onChange={handleUrlChange}
              placeholder="Or enter image URL"
              className="url-input"
              disabled={uploading}
            />
            
            <div className="image-notes">
              <small>Supported formats: JPG, PNG, GIF, WebP â€¢ Max size: 5MB</small>
              {selectedFile && (
                <div className="file-info">
                  <small>Selected: {selectedFile.name}</small>
                </div>
              )}
            </div>
          </div>

          <div className="form-group">
            <label>Tags (comma separated)</label>
            <input
              type="text"
              value={formData.tags}
              onChange={(e) => setFormData({...formData, tags: e.target.value})}
              disabled={uploading}
            />
          </div>

          <div className="form-group checkbox">
            <label>
              <input
                type="checkbox"
                checked={formData.published}
                onChange={(e) => setFormData({...formData, published: e.target.checked})}
                disabled={uploading}
              />
              Published
            </label>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary" disabled={uploading}>
              {uploading ? 'Uploading...' : (blog ? 'Update Blog' : 'Create Blog')}
            </button>
            <button type="button" className="btn-secondary" onClick={onCancel}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectManager;
